CREATE OR REPLACE PROCEDURE proj_vincular_os_projeto_etapa ( nr_seq_ordem_p bigint, nr_seq_projeto_p bigint, ie_acao_p text, nm_usuario_p text, nr_seq_cron_etapa_p bigint) AS $body$
BEGIN

if (ie_acao_p = 'V') then
	insert into proj_ordem_servico(nr_sequencia,
		nr_seq_proj,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_ordem,
		nr_seq_cron_etapa,
		ie_origem_ordem)
	values (	nextval('proj_ordem_servico_seq'),
		nr_seq_projeto_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_ordem_p,
		nr_seq_cron_etapa_p,
		'P');
elsif (ie_acao_p = 'D') then
	delete	from proj_ordem_servico
	where	nr_seq_ordem 	= nr_seq_ordem_p
	and	nr_seq_proj	= nr_seq_projeto_p
	and	nr_seq_cron_etapa = nr_seq_cron_etapa_p;
end if;

commit;

end;
$body$
