SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_vincular_os_projeto ( nr_seq_ordem_p bigint, nr_seq_projeto_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_ordem_serv_pai_w		man_ordem_servico.nr_seq_ordem_serv_pai%type;
ie_possui_proj_vinc_w		varchar(1);

BEGIN

select	max(mos.nr_seq_ordem_serv_pai)
into STRICT	nr_seq_ordem_serv_pai_w
from	man_ordem_servico mos
where	mos.nr_sequencia = nr_seq_ordem_p;

if (nr_seq_ordem_serv_pai_w IS NOT NULL AND nr_seq_ordem_serv_pai_w::text <> '') then
	begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1085000);
	end;
end if;

CALL verifica_permite_add_os_proj(nr_seq_projeto_p, nr_seq_ordem_p);

if (ie_acao_p = 'V') then

	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_proj_vinc_w
	from	proj_ordem_servico
	where	nr_seq_ordem = nr_seq_ordem_p
	and		nr_seq_proj = nr_seq_projeto_p;
	
	if (ie_possui_proj_vinc_w = 'S') then
		begin
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1141357);
		end;
	end if;	
	
	insert into proj_ordem_servico(nr_sequencia,
		nr_seq_proj,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_ordem,
		nr_seq_cron_etapa,
		ie_origem_ordem)
	values (	nextval('proj_ordem_servico_seq'),
		nr_seq_projeto_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_ordem_p,
		null,
		'P');
		
elsif (ie_acao_p = 'D') then
	delete	from proj_ordem_servico
	where	nr_seq_ordem 	= nr_seq_ordem_p
	and	nr_seq_proj	= nr_seq_projeto_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
