CREATE OR REPLACE PROCEDURE prot_sc_ativa_inativa ( nr_sequencia_p bigint, ie_acao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
ie_acao_p	0 - ativa protocolo solicitação compra
		1 - inativa protocolo solicitação compra
*/
BEGIN

if (ie_acao_p = 0) then
	update	prot_solic_compra
	set		ie_situacao = 'A',
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;
elsif (ie_acao_p = 1) then
	update	prot_solic_compra
	set		ie_situacao = 'I',
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
