CREATE OR REPLACE PROCEDURE prp_gerar_etapas_fase (nr_seq_regra_fase_p bigint, nr_seq_tipo_projeto_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_fase_processo_w	prp_fase_processo_etapa.nr_seq_fase_processo%type;

c01 CURSOR FOR
	SELECT	a.nr_seq_etapa_processo
	from	prp_fase_processo_etapa a
	where	a.nr_seq_fase_processo = nr_seq_fase_processo_w
	and     	a.ie_situacao = 'A'
	and (exists (SELECT	1
			from	prp_necessidade_etapa b
			where	a.nr_seq_etapa_processo = b.nr_seq_etapa_processo
			and	b.nr_seq_tipo_projeto = nr_seq_tipo_projeto_p) or coalesce(nr_seq_tipo_projeto_p::text, '') = '');

BEGIN

if (nr_seq_regra_fase_p IS NOT NULL AND nr_seq_regra_fase_p::text <> '') then

	select	nr_seq_fase_processo
	into STRICT	nr_seq_fase_processo_w
	from	prp_regra_tempo_fase
	where	nr_sequencia = nr_seq_regra_fase_p;

	delete	FROM prp_regra_tempo_etapa
	where	nr_seq_regra_fase = nr_seq_regra_fase_p;

	for	r_C01_w in C01 loop
		insert	into	prp_regra_tempo_etapa(	nr_seq_etapa_processo,
							nr_seq_regra_fase,
							nr_sequencia,
							qt_dias,
							dt_atualizacao,
							dt_atualizacao_nrec,
							nm_usuario,
							nm_usuario_nrec)
						values (	r_c01_w.nr_seq_etapa_processo,
							nr_seq_regra_fase_p ,
							nextval('prp_regra_tempo_etapa_seq'),
							1,
							clock_timestamp(),
							clock_timestamp(),
							nm_usuario_p,
							nm_usuario_p);
	end loop;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
