CREATE OR REPLACE PROCEDURE ptu_alterar_data_repasse ( nr_seq_intercambio_p bigint, nr_seq_benef_p bigint, dt_repasse_p timestamp, ie_tipo_beneficiario_p bigint, ie_tipo_alteracao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
ie_tipo_alteracao_p
1 - Somente Pr√©-pagamento
2 - Somente Custo operacional
3 - Selecionados
4 - Todos
*/
ie_tipo_beneficiario_w	varchar(10);


BEGIN

select	CASE WHEN ie_tipo_beneficiario_p=0 THEN 'I' WHEN ie_tipo_beneficiario_p=1 THEN 'A' WHEN ie_tipo_beneficiario_p=2 THEN 'E' WHEN ie_tipo_beneficiario_p=3 THEN 'T' END
into STRICT	ie_tipo_beneficiario_w
;

if (ie_tipo_alteracao_p	= 1) then
	update	ptu_intercambio_benef	a
	set	dt_repasse		= dt_repasse_p
	where	ie_repasse		= 'P'
	and	ie_status		= 'I'
	and	((ie_tipo_registro	= ie_tipo_beneficiario_w) or (ie_tipo_beneficiario_w = 'T'))
	and	exists (	SELECT	1
				from	ptu_intercambio_empresa	p,
					ptu_intercambio		q
				where	p.nr_seq_intercambio	= nr_seq_intercambio_p
				and	a.nr_seq_empresa	= p.nr_sequencia);

elsif (ie_tipo_alteracao_p	= 2) then
	update	ptu_intercambio_benef	a
	set	dt_repasse	= dt_repasse_p
	where	ie_repasse	= 'C'
	and	ie_status	= 'I'
	and	((ie_tipo_registro	= ie_tipo_beneficiario_w) or (ie_tipo_beneficiario_w = 'T'))
	and	exists (	SELECT	1
				from	ptu_intercambio_empresa	p,
					ptu_intercambio		q
				where	p.nr_seq_intercambio	= nr_seq_intercambio_p
				and	a.nr_seq_empresa	= p.nr_sequencia);

elsif (ie_tipo_alteracao_p	= 3) then
	update	ptu_intercambio_benef	a
	set	dt_repasse	= dt_repasse_p
	where	nr_sequencia	= nr_seq_benef_p
	and	((ie_tipo_registro	= ie_tipo_beneficiario_w) or (ie_tipo_beneficiario_w = 'T'))
	and	ie_status	= 'I';
elsif (ie_tipo_alteracao_p	= 4) then
	update	ptu_intercambio_benef	a
	set	dt_repasse	= dt_repasse_p
	where	ie_status	= 'I'
	and	((ie_tipo_registro	= ie_tipo_beneficiario_w) or (ie_tipo_beneficiario_w = 'T'))
	and	exists (	SELECT	1
				from	ptu_intercambio_empresa	p,
					ptu_intercambio		q
				where	p.nr_seq_intercambio	= nr_seq_intercambio_p
				and	a.nr_seq_empresa	= p.nr_sequencia);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
