SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_alterar_mes_comp_fatura ( nr_seq_fatura_p bigint, dt_mes_fatura_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Alterar o mês de competência da fatura.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
	Consistir se o mês está fechado.
Referências:
	OPS - Contas intercâmbio
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_fechados_w			bigint;
dt_mes_competencia_w		timestamp;
nr_seq_protocolo_w		ptu_fatura.nr_seq_protocolo%type;


BEGIN
dt_mes_competencia_w	:= trunc(dt_mes_fatura_p);

select	count(1)
into STRICT	qt_fechados_w
from	ctb_mes_ref	a
where	a.dt_referencia	= dt_mes_competencia_w
and	(a.dt_fechamento IS NOT NULL AND a.dt_fechamento::text <> '');

if (qt_fechados_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(257474);
end if;

update	ptu_fatura
set	dt_mes_competencia	= dt_mes_competencia_w,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_fatura_p;

select	max(nr_seq_protocolo)
into STRICT	nr_seq_protocolo_w
from	ptu_fatura
where	nr_sequencia = nr_seq_fatura_p;

if (nr_seq_protocolo_w IS NOT NULL AND nr_seq_protocolo_w::text <> '') then
	CALL pls_alterar_dados_protocolo(nr_seq_protocolo_w, dt_mes_competencia_w, 'DR', nm_usuario_p, cd_estabelecimento_p, 'N');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
