CREATE OR REPLACE PROCEDURE ptu_alterar_segurado_repasse ( nr_seq_lote_envio_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_tipo_segurado_ant_w		varchar(1);
nr_seq_segurado_w		bigint;
qt_registros_emissor_w		bigint;
nr_seq_titular_w		bigint;
dt_validade_w			timestamp;
dt_validade_cart_parmetros_w	varchar(1);
ie_gerar_validade_cartao_w	varchar(1);
dt_contratacao_w		timestamp;
dt_contrato_w			timestamp;

C01 CURSOR FOR
	SELECT	distinct c.nr_seq_segurado
	from	ptu_intercambio_benef	c,
		ptu_intercambio_empresa	b,
		ptu_intercambio		a
	where	c.nr_seq_empresa	= b.nr_sequencia
	and	b.nr_seq_intercambio	= a.nr_sequencia
	and	a.nr_seq_lote_envio	= nr_seq_lote_envio_p;


BEGIN

select	coalesce(dt_base_validade_carteira,'B'),
	coalesce(ie_gerar_validade_cartao,'S')
into STRICT	dt_validade_cart_parmetros_w,
	ie_gerar_validade_cartao_w
from	pls_parametros
where	cd_estabelecimento	= cd_estabelecimento_p;

open C01;
loop
fetch C01 into
	nr_seq_segurado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	b.ie_tipo_segurado,
		b.nr_seq_titular,
		b.dt_contratacao,
		a.dt_contrato
	into STRICT	ie_tipo_segurado_ant_w,
		nr_seq_titular_w,
		dt_contratacao_w,
		dt_contrato_w
	from	pls_segurado	b,
		pls_contrato	a
	where	b.nr_seq_contrato	= a.nr_sequencia
	and	b.nr_sequencia		= nr_seq_segurado_w;

	update	pls_segurado
	set	ie_tipo_segurado	= 'R',
		dt_atualizacao		= clock_timestamp(),
		nm_usuario 		= nm_usuario_p,
		dt_alteracao_tipo_segurado = CASE WHEN ie_tipo_segurado='R' THEN  dt_alteracao_tipo_segurado  ELSE trunc(clock_timestamp(),'dd') END
	where	nr_sequencia		= nr_seq_segurado_w;

	CALL pls_gerar_segurado_historico(	nr_seq_segurado_w, '30', clock_timestamp(), 'Alteração de repasse de: '|| obter_valor_dominio(2406,ie_tipo_segurado_ant_w)||' para: '||obter_valor_dominio(2406,'R'),
					'ptu_alterar_segurado_repasse', null, null, null,
					null, null, null, null,
					null, null, null, null,
					nm_usuario_p, 'N');
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
