SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_atualizar_datas_imp_benef ( nr_seq_benef_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_repasse_w		varchar(1);
dt_inclusao_w		timestamp;
dt_exclusao_w		timestamp;
dt_geracao_w		timestamp;
nr_seq_empresa_w	bigint;


BEGIN

select	ie_repasse,
	dt_repasse,
	dt_fim_repasse,
	nr_seq_empresa
into STRICT	ie_repasse_w,
	dt_inclusao_w,
	dt_exclusao_w,
	nr_seq_empresa_w
from	ptu_intercambio_benef
where	nr_sequencia	= nr_seq_benef_p;

if (ie_repasse_w	= 'P') then
	if (dt_inclusao_w IS NOT NULL AND dt_inclusao_w::text <> '') then
		dt_inclusao_w	:= trunc(dt_inclusao_w,'Month');
		dt_inclusao_w	:= add_months(dt_inclusao_w,1);
	end if;
	if (dt_exclusao_w IS NOT NULL AND dt_exclusao_w::text <> '') then
		dt_exclusao_w	:= fim_mes(dt_exclusao_w);
	end if;
elsif (ie_repasse_w	= 'C') then
	select	max(dt_geracao)
	into STRICT	dt_geracao_w
	from	ptu_intercambio		b,
		ptu_intercambio_empresa	a
	where	a.nr_seq_intercambio	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_empresa_w;

	dt_inclusao_w		:= clock_timestamp();

	if (dt_exclusao_w IS NOT NULL AND dt_exclusao_w::text <> '') then
		if (dt_exclusao_w	< dt_geracao_w) then
			dt_exclusao_w	:= dt_geracao_w;
		end if;
	end if;
end if;

update	ptu_intercambio_benef
set	dt_repasse	= dt_inclusao_w,
	dt_fim_repasse	= dt_exclusao_w
where	nr_sequencia	= nr_seq_benef_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
