SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_atualizar_xml_gpi_ws ( ie_tipo_transacao_p bigint, nr_seq_xml_gpi_p pls_xml_integracao.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type ) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Atualizar cabeçalho do XML gerado, realizar o controle entre a transação e o
arquivo XML gerado na base.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x Tasy (Delphi/Java) [ x ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:  Esta procedure deve ser utilizada somente no momento de gerar o XML
para enviar para alguma outra operadora ou no momento de gerar o retorno de um arquivo
recebido.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_tipo_transacao_w	varchar(255)	:= 'mensagemXML';
ds_xml_w		pls_xml_integracao.ds_xml%type;


BEGIN

--001 - Solicitar Protocolo
if ( ie_tipo_transacao_p = 1 ) then
	ds_tipo_transacao_w := 'solicitarProtocoloWS';
--002 - Resposta de Solicitação de Protocolo
elsif ( ie_tipo_transacao_p = 2) then
	ds_tipo_transacao_w := 'respostasolicitarProtocoloWS';
--003 - Complementar Protocolo
elsif ( ie_tipo_transacao_p = 3 ) then
	ds_tipo_transacao_w := 'complementoProtocoloWS';
--004 - Confirmação
elsif ( ie_tipo_transacao_p = 4 ) then
	ds_tipo_transacao_w := 'confirmacaoWS';
--005 - Responder Atendimento
elsif ( ie_tipo_transacao_p = 5 ) then
	ds_tipo_transacao_w := 'responderAtendimentoWS';
--006 - Consulta Status Protocolo
elsif ( ie_tipo_transacao_p = 6 ) then
	ds_tipo_transacao_w := 'consultaStatusProtocoloWS';
--007 - Resposta da Consulta de Status de Protocolo
elsif ( ie_tipo_transacao_p = 7 ) then
	ds_tipo_transacao_w := 'respostaConsultaStatusProtocoloWS';
--008 - Consulta Histórico
elsif ( ie_tipo_transacao_p = 8 ) then
	ds_tipo_transacao_w := 'consultaHistoricoWS';
--009 - Resposta da Consulta Histórico
elsif ( ie_tipo_transacao_p = 9 ) then
	ds_tipo_transacao_w := 'respostaConsultaHistoricoWS';
--010 - Cancelamento
elsif ( ie_tipo_transacao_p = 10 ) then
	ds_tipo_transacao_w := 'cancelamentoWS';
-- 011 - Erro Inesperado
elsif ( ie_tipo_transacao_p = 11 ) then
	ds_tipo_transacao_w := 'erroInesperadoWS';
--012 - Encaminhar Execução
elsif ( ie_tipo_transacao_p = 12 ) then
	ds_tipo_transacao_w := 'encaminharExecucaoWS';
end if;

if (nr_seq_xml_gpi_p IS NOT NULL AND nr_seq_xml_gpi_p::text <> '') then
	select	ds_xml
	into STRICT	ds_xml_w
	from	pls_xml_integracao
	where	nr_sequencia	= nr_seq_xml_gpi_p;

	--Atualizaca o cabeçalho do arquivo XML antes de enviar, para ficar igual ao padrão do WSDL
	ds_xml_w := replace(ds_xml_w, 'mensagemXML', ds_tipo_transacao_w);

	update	pls_xml_integracao
	set	ds_xml 		= ds_xml_w,
		dt_atualizacao 	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_xml_gpi_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ptu_atualizar_xml_gpi_ws ( ie_tipo_transacao_p bigint, nr_seq_xml_gpi_p pls_xml_integracao.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type ) FROM PUBLIC;

