SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_atualiza_geracao_arq_a100 ( nr_sequencial_p bigint, ie_tipo_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
ie_tipo_p
1 - Lote
2 - Item
3 - Item - Importação do retorno
*/
qt_registros_w		bigint;


BEGIN

if (ie_tipo_p	= 1) then
	select	count(*)
	into STRICT	qt_registros_w
	from	ptu_intercambio
	where	nr_seq_lote_envio	= nr_sequencial_p
	and	coalesce(dt_geracao_arquivo::text, '') = '';
	/*Atualizar a data de geração do arquivo,. caso todos os arquivos foram gerados*/

	if (qt_registros_w = 0) then
		update	ptu_intercambio_lote_envio
		set	dt_geracao_arquivo	= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_sequencial_p;
	end if;
elsif (ie_tipo_p	= 2) then
	update	ptu_intercambio
	set	dt_geracao_arquivo	= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_sequencial_p;
elsif (ie_tipo_p	= 3) then
	update	ptu_intercambio
	set	DT_IMPORTACAO_RETORNO	= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_sequencial_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
