SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_atualiza_resposta_aud_v60 ( nr_seq_resp_aud_p ptu_resposta_auditoria.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

 
nr_seq_guia_w		pls_guia_plano.nr_sequencia%type;
nr_seq_requisicao_w	pls_requisicao.nr_sequencia%type;
nr_seq_execucao_w	ptu_resposta_auditoria.nr_seq_execucao%type;


BEGIN 
 
--Selec utilizado para verificar se a auditoria foi gerada na autorização ou na requisição 
select	a.nr_seq_guia, 
	a.nr_seq_requisicao, 
	a.nr_seq_execucao 
into STRICT	nr_seq_guia_w, 
	nr_seq_requisicao_w, 
	nr_seq_execucao_w 
from	ptu_resposta_auditoria a 
where	a.nr_sequencia 	= nr_seq_resp_aud_p;
 
if (nr_seq_guia_w IS NOT NULL AND nr_seq_guia_w::text <> '') then 
	--Rotina utilizada para atualizar as informações na OPS - Autorização e na auditoria existente 
	CALL ptu_atualiza_guia_auditoria( nr_seq_guia_w, nr_seq_resp_aud_p, nm_usuario_p,cd_estabelecimento_p );
 
	-- Inserir as glosas das inconsistências PTU, é necessário chamar esta rotina após atualizar o status da requisição e da guia 
	CALL pls_obter_glosa_incons(nr_seq_guia_w, 'G', nm_usuario_p);
elsif (nr_seq_requisicao_w IS NOT NULL AND nr_seq_requisicao_w::text <> '') then 
	--Rotina utilizada para atualizar as informações na OPS - Requisição para Autorização e na auditoria existente 
	CALL ptu_atualiza_req_auditoria( nr_seq_requisicao_w, nr_seq_resp_aud_p, nm_usuario_p,cd_estabelecimento_p );
 
	-- Inserir as glosas das inconsistências PTU, é necessário chamar esta rotina após atualizar o status da requisição e da guia 
	CALL pls_obter_glosa_incons(nr_seq_requisicao_w, 'R', nm_usuario_p);
end if;
 
-- Gerar confirmação da transação tipo de respota RA- Resposta de Auditoria 
CALL ptu_gestao_envio_confirmacao(nr_seq_execucao_w, cd_estabelecimento_p, 'RA', nm_usuario_p);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
