SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_autoriza_guia_intercambio ( nr_seq_guia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_status_w			varchar(2);
ie_estagio_w			smallint;
qt_itens_aprov_w		bigint;
qt_itens_negado_w		bigint;
qt_proc_negado_w		bigint;
qt_mat_negado_w			bigint;
qt_proc_aprov_w			bigint;
qt_mat_aprov_w			bigint;
dt_validade_w			varchar(255);
dt_validade_senha_w		timestamp;
cd_senha_w			varchar(255);
ie_tipo_processo_w		varchar(255);
nr_seq_segurado_w		bigint;
dt_solicitacao_w		timestamp;
ie_tipo_guia_w			varchar(255);
qt_dias_validade_w		bigint;


BEGIN 
 
select	ie_status, 
	ie_estagio, 
	ie_tipo_processo, 
	nr_seq_segurado, 
	dt_solicitacao, 
	ie_tipo_guia 
into STRICT	ie_status_w, 
	ie_estagio_w, 
	ie_tipo_processo_w, 
	nr_seq_segurado_w, 
	dt_solicitacao_w, 
	ie_tipo_guia_w 
from	pls_guia_plano 
where	nr_sequencia	= nr_seq_guia_p;
 
if (ie_tipo_processo_w <> 'P') then 
	qt_dias_validade_w := (obter_valor_param_usuario(1204, 3, Obter_Perfil_Ativo, nm_usuario_p, 0))::numeric;
else	 
	qt_dias_validade_w := (pls_obter_param_padrao_funcao(11, 1247))::numeric;
end if;
 
if (ie_status_w	= '2') and (ie_estagio_w	= 3) then 
	update	pls_guia_plano 
	set	ie_status		= '1', 
		ie_estagio		= 6, 
		dt_autorizacao		= clock_timestamp(), 
		dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p 
	where	nr_sequencia		= nr_seq_guia_p;
 
	update	pls_guia_plano_proc 
	set	ie_status		= 'S', 
		dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p 
	where	nr_seq_guia		= nr_seq_guia_p;
 
	update	pls_guia_plano_mat 
	set	ie_status		= 'S', 
		dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p 
	where	nr_seq_guia		= nr_seq_guia_p;
	 
	CALL pls_atualizar_estagio_guia(	nr_seq_guia_p,nm_usuario_p,null);
	SELECT * FROM pls_gerar_validade_senha(	nr_seq_guia_p, nr_seq_segurado_w, qt_dias_validade_w, dt_solicitacao_w, ie_tipo_guia_w, nm_usuario_p, dt_validade_w, cd_senha_w) INTO STRICT dt_validade_w, cd_senha_w;
					 
	begin 
		select	to_date(dt_validade_w,'dd/mm/yyyy') 
		into STRICT	dt_validade_senha_w 
		;
	exception 
	when others then 
		dt_validade_senha_w	:= null;
	end;
	 
	update	pls_guia_plano 
	set	cd_senha		= cd_senha_w, 
		dt_validade_senha	= dt_validade_senha_w, 
		dt_atualizacao		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p 
	where	nr_sequencia		= nr_seq_guia_p;
elsif (ie_status_w	= '2') and (ie_estagio_w	= 2) then 
	select	count(1) 
	into STRICT	qt_proc_negado_w 
	from	pls_guia_plano_proc 
	where	nr_seq_guia		= nr_seq_guia_p 
	and	ie_status		in ('M','N','K');
	 
	select	count(1) 
	into STRICT	qt_mat_negado_w 
	from	pls_guia_plano_mat 
	where	nr_seq_guia		= nr_seq_guia_p 
	and	ie_status		in ('M','N','K');
	 
	select	count(1) 
	into STRICT	qt_proc_aprov_w 
	from	pls_guia_plano_proc 
	where	nr_seq_guia		= nr_seq_guia_p 
	and	ie_status		in ('L','S','P');
	 
	select	count(1) 
	into STRICT	qt_mat_aprov_w 
	from	pls_guia_plano_mat 
	where	nr_seq_guia		= nr_seq_guia_p 
	and	ie_status		in ('L','S','P');
	 
	qt_itens_negado_w	:= qt_proc_negado_w + qt_mat_negado_w;
	qt_itens_aprov_w	:= qt_proc_aprov_w + qt_mat_aprov_w;
 
	if (qt_itens_negado_w	> 0) and (qt_itens_aprov_w	> 0) then 
		update	pls_guia_plano 
		set	ie_status		= '1', 
			ie_estagio		= 10, 
			dt_atualizacao		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p 
		where	nr_sequencia		= nr_seq_guia_p;
		 
		CALL pls_atualizar_estagio_guia(nr_seq_guia_p,nm_usuario_p,null);
		SELECT * FROM pls_gerar_validade_senha(	nr_seq_guia_p, nr_seq_segurado_w, qt_dias_validade_w, dt_solicitacao_w, ie_tipo_guia_w, nm_usuario_p, dt_validade_w, cd_senha_w) INTO STRICT dt_validade_w, cd_senha_w;
 
		begin 
			select	to_date(dt_validade_w,'dd/mm/yyyy') 
			into STRICT	dt_validade_senha_w 
			;
		exception 
		when others then 
			dt_validade_senha_w	:= null;
		end;
 
		update	pls_guia_plano 
		set	cd_senha		= cd_senha_w, 
			dt_validade_senha	= dt_validade_senha_w, 
			dt_atualizacao		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p 
		where	nr_sequencia		= nr_seq_guia_p;
	else 
		update	pls_guia_plano 
		set	ie_status		= '3', 
			ie_estagio		= 4, 
			dt_atualizacao		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p 
		where	nr_sequencia		= nr_seq_guia_p;
		 
		CALL pls_atualizar_estagio_guia(nr_seq_guia_p,nm_usuario_p,null);
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
