SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_cd_empresa_intercambio ( nr_seq_intercambio_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		bigint;
cd_cgc_w		varchar(14);
cd_pessoa_fisica_w	varchar(10);
ie_tipo_pessoa_w	varchar(2);
ie_abrangencia_w	varchar(3);
nr_seq_regra_w		bigint;
cd_atual_w		bigint;
cd_atual_ww		bigint;
cd_inicial_w		bigint;
cd_final_w		bigint;
ie_gerar_codigo_w	varchar(1)	:= 'S';
cd_fixo_w		bigint;
ie_valor_valido_w	varchar(1)	:= 'N';
qt_registros_pj_w	bigint;
cd_operadora_empresa_w	varchar(10);
cd_empresa_informado_w	pls_intercambio.cd_operadora_empresa%type;
ie_tipo_contrato_w	pls_intercambio.ie_tipo_contrato%type;
nr_seq_plano_w		pls_plano.nr_sequencia%type;
ie_tipo_contratacao_w	pls_plano.ie_tipo_contratacao%type;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_carteira_controle_iden
	where	((ie_tipo_pessoa = ie_tipo_pessoa_w) or (ie_tipo_pessoa = 'A'))
	and	ie_situacao	= 'A'
	and	ie_tipo_regra	= 'E'
	and	coalesce(ie_abrangencia,coalesce(ie_abrangencia_w,'0'))	= coalesce(ie_abrangencia_w,'0')
	and	cd_estabelecimento	= cd_estabelecimento_p
	and	((ie_tipo_contrato = 'I') or (coalesce(ie_tipo_contrato::text, '') = ''))
	order by
		ie_tipo_pessoa,
		ie_tipo_contratacao,
		coalesce(ie_abrangencia,coalesce(ie_abrangencia_w,'0')),
		ie_tipo_contrato;

BEGIN

select	cd_cgc,
	cd_pessoa_fisica,
	CASE WHEN coalesce(cd_cgc::text, '') = '' THEN 'PF'  ELSE 'PJ' END ,
	cd_operadora_empresa,
	ie_tipo_contrato
into STRICT	cd_cgc_w,
	cd_pessoa_fisica_w,
	ie_tipo_pessoa_w,
	cd_empresa_informado_w,
	ie_tipo_contrato_w
from	pls_intercambio
where	nr_sequencia	= nr_seq_intercambio_p;

select	count(*)
into STRICT	qt_registros_w
from	pls_intercambio
where	coalesce(cd_operadora_empresa,'0') <> '0'
and	nr_sequencia	= nr_seq_intercambio_p;

if (qt_registros_w	= 0) then
	select	max(nr_seq_plano)
	into STRICT	nr_seq_plano_w
	from	pls_intercambio_plano
	where	nr_seq_intercambio	= nr_seq_intercambio_p;
	
	if (nr_seq_plano_w IS NOT NULL AND nr_seq_plano_w::text <> '') then
		select	ie_abrangencia,
			ie_tipo_contratacao
		into STRICT	ie_abrangencia_w,
			ie_tipo_contratacao_w
		from	pls_plano
		where	nr_sequencia = nr_seq_plano_w;
	else
		ie_tipo_contratacao_w	:= null;
		ie_abrangencia_w	:= null;
	end if;
	
	for r_c01_w in c01 loop
		begin
		nr_seq_regra_w	:= r_c01_w.nr_sequencia;
		end;
	end loop;
	
	if (coalesce(nr_seq_regra_w,0) <> 0) then
		select	cd_atual,
			cd_inicial,
			cd_final,
			cd_fixo
		into STRICT	cd_atual_w,
			cd_inicial_w,
			cd_final_w,
			cd_fixo_w
		from	pls_carteira_controle_iden
		where	nr_sequencia	= nr_seq_regra_w;
		
		if (ie_tipo_pessoa_w	= 'PJ') then
			select	max(cd_operadora_empresa)
			into STRICT	cd_operadora_empresa_w
			from	pessoa_juridica
			where	cd_cgc	= cd_cgc_w;
			
			if (coalesce(cd_operadora_empresa_w,0) > 0) then
				ie_gerar_codigo_w	:= 'N';
				cd_atual_ww		:= cd_operadora_empresa_w;
			end if;
		end if;

		if (ie_gerar_codigo_w	= 'S') then
			if (coalesce(cd_fixo_w,-999) <> -999) then
				cd_atual_ww	:= cd_fixo_w;
			else
				if (coalesce(cd_atual_w,0)	= 0) then
					cd_atual_ww	:= cd_inicial_w;
				else
					cd_atual_ww	:= cd_atual_w + 1;
				end if;
				
				/* Verificar se o valor nao esta sendo utilizado em outros intercambios */

				while(ie_valor_valido_w = 'N') loop
					begin
					if (cd_atual_ww < cd_inicial_w) then
						cd_atual_ww	:= cd_inicial_w;
					end if;
					
					select	count(*)
					into STRICT	qt_registros_w
					from	pls_intercambio
					where	cd_operadora_empresa	= cd_atual_ww;
					
					if (qt_registros_w = 0) then
						select	count(1)
						into STRICT	qt_registros_w
						from	pls_contrato
						where	cd_operadora_empresa	= cd_atual_ww;
					end if;
					
					select	count(*)
					into STRICT	qt_registros_pj_w
					from	pessoa_juridica
					where	cd_operadora_empresa	= cd_atual_ww;
					
					if (qt_registros_w > 0) or (qt_registros_pj_w > 0) then
						cd_atual_ww		:= cd_atual_ww + 1;
					else
						ie_valor_valido_w	:= 'S';
					end if;
					end;
				end loop;
				
				if	not(cd_atual_ww >= cd_inicial_w AND cd_atual_ww <= cd_final_w) then
					CALL Wheb_mensagem_pck.exibir_mensagem_abort(266992);
				end if;
				
				update	pls_carteira_controle_iden
				set	cd_atual	= cd_atual_ww
				where	nr_sequencia	= nr_seq_regra_w;
			end if;
			
			if (coalesce(cd_cgc_w,'0') <> '0') then
				update	pessoa_juridica
				set	cd_operadora_empresa	= cd_atual_ww
				where	cd_cgc			= cd_cgc_w;
			end if;
		end if;
		
		update	pls_intercambio
		set	cd_operadora_empresa	= cd_atual_ww
		where	nr_sequencia		= nr_seq_intercambio_p;
	end if;
else
	cd_atual_ww	:= cd_empresa_informado_w;
end if;

if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') and (ie_tipo_contrato_w = 'F') then
	CALL pls_inserir_cod_empresa(cd_cgc_w, cd_atual_ww, nm_usuario_p, 'N');
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ptu_cd_empresa_intercambio ( nr_seq_intercambio_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

