SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_conferir_doc_fisico_cobr ( nr_seq_nota_cobr_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_fatura_w			bigint;
qt_notas_doc_fisico_w		bigint;
qt_notas_conf_w			bigint;
ie_doc_fisico_conf_w		varchar(1);
ie_necessita_doc_fisico_w	varchar(1);


BEGIN
select	max(nr_seq_fatura)
into STRICT	nr_seq_fatura_w
from	ptu_nota_cobranca
where	nr_sequencia		= nr_seq_nota_cobr_p;

select	count(*)
into STRICT	qt_notas_doc_fisico_w
from	ptu_nota_cobranca
where	nr_seq_fatura		= nr_seq_fatura_w
and	ie_necessita_doc_fisico = 'S';

if (qt_notas_doc_fisico_w > 0) then
	update	ptu_fatura
	set	ie_doc_fisica_conferida	= 'N',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_fatura_w;
end if;

select	max(ie_doc_fisico_conf)
into STRICT	ie_doc_fisico_conf_w
from	ptu_nota_cobranca
where	nr_sequencia		= nr_seq_nota_cobr_p;

if (ie_doc_fisico_conf_w = 'S') then
	update	ptu_nota_cobranca
	set	ie_doc_fisico_conf	= 'N',
		dt_conferencia_doc	 = NULL,
		nm_usuario_doc_fisico	 = NULL
	where	nr_sequencia		= nr_seq_nota_cobr_p;
else
	update	ptu_nota_cobranca
	set	ie_doc_fisico_conf	= 'S',
		dt_conferencia_doc	= clock_timestamp(),
		nm_usuario_doc_fisico	= nm_usuario
	where	nr_sequencia		= nr_seq_nota_cobr_p;
end if;

select	count(*)
into STRICT	qt_notas_conf_w
from	ptu_nota_cobranca
where	nr_seq_fatura		= nr_seq_fatura_w
and	ie_doc_fisico_conf	= 'S'
and	ie_necessita_doc_fisico = 'S';

if (qt_notas_doc_fisico_w = qt_notas_conf_w) and (qt_notas_doc_fisico_w > 0) then

	update	ptu_fatura
	set	ie_doc_fisica_conferida	= 'S',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_fatura_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
