CREATE OR REPLACE PROCEDURE ptu_consistir_pedido_autor ( nr_seq_requisicao_p bigint, nr_seq_guia_p bigint, nr_seq_pedido_aut_p bigint, nm_usuario_p text ) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Validar os campos obrigatórios na transação de Pedido de autorização do PTU 6.0

-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  x]  Objetos do dicionário [ ] Tasy (Delphi/Java) [   ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
Performace;
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_tipo_rede_min_w		ptu_pedido_autorizacao.ie_tipo_rede_min%type;
cd_unimed_prestador_req_w	ptu_pedido_autorizacao.cd_unimed_prestador_req%type;
nr_seq_prestador_req_w		ptu_pedido_autorizacao.nr_seq_prestador_req%type;
cd_unimed_prestador_w		ptu_pedido_autorizacao.cd_unimed_prestador%type;
nr_seq_prest_alto_custo_w	ptu_pedido_autorizacao.nr_seq_prest_alto_custo%type;
nm_prestador_alto_custo_w	ptu_pedido_autorizacao.nm_prestador_alto_custo%type;
ie_urg_emerg_w			ptu_pedido_autorizacao.ie_urg_emerg%type;
ie_liminar_w			ptu_pedido_autorizacao.ie_liminar%type;
cd_municipio_ibge_w		ptu_pedido_autorizacao.cd_municipio_ibge%type;
nr_versao_tiss_w		ptu_pedido_autorizacao.nr_versao_tiss%type;
nr_seq_prestador_web_w		pls_requisicao.nr_seq_prestador_web%type;
nr_seq_prestador_w		pls_requisicao.nr_seq_prestador%type;
ie_tipo_guia_w			pls_requisicao.ie_tipo_guia%type;
nr_seq_clinica_w		pls_requisicao.nr_seq_clinica%type;
ds_indicacao_clinica_w		pls_requisicao.ds_indicacao_clinica%type;
ie_recem_nascido_w		pls_requisicao.ie_recem_nascido%type;
dt_internacao_sugerida_w	pls_requisicao.dt_internacao_sugerida%type;
ie_origem_solic_w		pls_requisicao.ie_origem_solic%type;
ie_carater_atend_w		pls_guia_plano.ie_carater_internacao%type;
cd_estabelecimento_w		pls_outorgante.cd_estabelecimento%type;
cd_cgc_outorgante_w		pls_outorgante.cd_cgc_outorgante%type;
cd_cooperativa_w		pls_congenere.cd_cooperativa%type;
qt_servico_w			ptu_pedido_aut_servico.qt_servico%type;
vl_servico_w			ptu_pedido_aut_servico.vl_servico%type;
vl_uni_servico_w		ptu_pedido_aut_servico.vl_uni_servico%type;
nr_seq_prestador_exec_w		pls_requisicao.nr_seq_prestador_exec%type;
ds_opme_w			varchar(400);
ie_indicacao_acidente_w		bigint;
qt_prestador_w			bigint := 0;
qt_diagnostico_w		bigint := 0;
qt_opme_w			bigint := 0;
qt_radio_w			bigint := 0;
qt_quimio_w			bigint := 0;
qt_opme_ww			bigint := 0;
qt_radio_ww			bigint := 0;
qt_quimio_ww			bigint := 0;
qt_consist_w			bigint := 0;
C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_consistencia,
		ie_bloqueia_portal
	from	ptu_consitencia_envio
	where	ie_tipo_transacao 	= '0'
	and	ie_situacao		= 'A'
	order by 1;

C02 CURSOR FOR
	SELECT	nr_sequencia,
		ie_tipo_anexo
	from	pls_lote_anexo_guias_aut
	where	((nr_seq_requisicao IS NOT NULL AND nr_seq_requisicao::text <> '' AND nr_seq_requisicao = nr_seq_requisicao_p)
	or (nr_seq_guia IS NOT NULL AND nr_seq_guia::text <> '') and (nr_seq_guia = nr_seq_guia_p));

--Anexo OPME
C03 CURSOR( nr_seq_anexo_pc 	pls_lote_anexo_guias_aut.nr_sequencia%type ) FOR
	SELECT	b.nr_seq_material,
		a.ds_justificativa,
		a.nm_profissional_solic,
		a.nr_telef_prof_solic,
		b.nr_registro_anvisa,
		b.cd_ref_fabricante_imp,
		b.ie_opcao_fabricante
	from	pls_lote_anexo_guias_aut 	a,
		pls_lote_anexo_mat_aut		b
	where	b.nr_seq_lote_anexo_guia	= a.nr_sequencia
	and	a.nr_sequencia			= nr_seq_anexo_pc;

--Anexo Quimio
C04 CURSOR( nr_seq_anexo_pc 	pls_lote_anexo_guias_aut.nr_sequencia%type ) FOR
	SELECT	a.qt_idade_benef,
		a.ie_sexo,
		a.nm_profissional_solic,
		a.nr_telef_prof_solic,
		a.qt_altura,
		a.qt_peso,
		a.qt_superficie_corporal,
		a.nr_ciclo_previsto,
		a.nr_ciclo_atual,
		a.qt_intervalo_ciclo,
		a.nr_dia_ciclo_atual,
		a.ds_procedimento_cirurgico,
		c.dt_prevista,
		c.qt_unidade_medida,
		c.ie_via_administracao,
		c.ie_frequencia_dose,
		c.qt_dosagem_total
	from	pls_lote_anexo_guias_aut	a,
		pls_lote_anexo_mat_aut		c
	where	c.nr_seq_lote_anexo_guia 	= a.nr_sequencia
	and	a.nr_sequencia			= nr_seq_anexo_pc;
--Anexo Radio
C05 CURSOR( nr_seq_anexo_pc 	pls_lote_anexo_guias_aut.nr_sequencia%type ) FOR
	SELECT	qt_idade_benef,
		ie_sexo,
		nm_profissional_solic,
		nr_telef_prof_solic,
		nr_campos,
		qt_dose_dia,
		qt_dose_total,
		qt_dias_previsto,
		dt_inicio_previsto
	from	pls_lote_anexo_guias_aut
	where	nr_sequencia	= nr_seq_anexo_pc;

--Diagnostico Quimio/radio. Este cursor não foi vinculado ao cursor de Quimio/radio pois a tabela em questão só recebe valor ao inserir algum registro manualmente, portanto, o cursor acima ficaria inválida.
C06 CURSOR( nr_seq_anexo_pc 	pls_lote_anexo_guias_aut.nr_sequencia%type ) FOR
	SELECT	ie_tipo_diagnostico,
		ds_plano_terapeutico,
		ie_estadia_tumor,
		ie_tipo_quimioterapia,
		cd_finalidade_tratamento,
		ie_capacidade_funcional,
		ie_tumor,
		ie_nodulo,
		ie_metastases
	from	pls_lote_anexo_diag_aut
	where	nr_seq_lote_anexo_guia 	= nr_seq_anexo_pc;
BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	cd_medico_solicitante, --nr_seq_prestador_req_w
		nr_seq_clinica,
		ie_recem_nascido,
		dt_internacao_sugerida,
		ie_tipo_guia,
		nr_seq_prestador,--nr_seq_prest_alto_custo_w/--nr_seq_prestador_web_w
		ie_origem_solic,
		ds_indicacao_clinica,
		ie_carater_internacao
	into STRICT	nr_seq_prestador_req_w,
		nr_seq_clinica_w,
		ie_recem_nascido_w,
		dt_internacao_sugerida_w,
		ie_tipo_guia_w,
		nr_seq_prestador_web_w,
		ie_origem_solic_w,
		ds_indicacao_clinica_w,
		ie_carater_atend_w
	from	pls_guia_plano
	where	nr_sequencia = nr_seq_guia_p;

	begin
	    select  coalesce(ie_tipo_rede_min_ptu,1)
	    into STRICT    ie_tipo_rede_min_w
	    from    pls_prestador
	    where   nr_sequencia    = nr_seq_prestador_web_w;
	exception
	when others then
	    ie_tipo_rede_min_w := '1';
	end;

	select	max(cd_cgc_outorgante)
	into STRICT	cd_cgc_outorgante_w
	from	pls_outorgante;

	select	max(cd_cooperativa)
	into STRICT	cd_cooperativa_w
	from	pls_congenere
	where	cd_cgc			= cd_cgc_outorgante_w
	and	(cd_cooperativa IS NOT NULL AND cd_cooperativa::text <> '');

	if (nr_seq_prestador_req_w IS NOT NULL AND nr_seq_prestador_req_w::text <> '') then
		cd_unimed_prestador_req_w		:=	cd_cooperativa_w;
	end if;

	begin
		select	substr(coalesce(cd_prestador, nr_sequencia),1,8)
		into STRICT	nr_seq_prest_alto_custo_w
		from	pls_prestador
		where	nr_sequencia	= nr_seq_prestador_web_w;
	exception
	when others then
		nr_seq_prest_alto_custo_w	:= nr_seq_prestador_web_w;
	end;

	if (nr_seq_prest_alto_custo_w IS NOT NULL AND nr_seq_prest_alto_custo_w::text <> '') then
		cd_unimed_prestador_w		:=	cd_cooperativa_w;
		nm_prestador_alto_custo_w   := substr(pls_obter_dados_prestador(nr_seq_prestador_web_w,'N'),1,25);
	end if;

	if (ie_carater_atend_w	= 'U') then
		ie_urg_emerg_w	:= 'S';
	else
		ie_urg_emerg_w	:= 'N';
	end if;

	ie_liminar_w := 'N';

	cd_estabelecimento_w := ptu_obter_estab_padrao;

	select	max(cd_municipio_ibge)
	into STRICT	cd_municipio_ibge_w
	from	pessoa_juridica
	where	cd_cgc	= (	SELECT	cd_cgc_outorgante
				from	pls_outorgante
				where	cd_estabelecimento = cd_estabelecimento_w);

	select	pls_obter_versao_tiss
	into STRICT	nr_versao_tiss_w
	;

	select  count(1)
	into STRICT	qt_opme_w
	from  	pls_guia_plano_mat
	where  	nr_seq_guia = nr_seq_guia_p
	and  	ie_tipo_anexo = 'OP';

	select  count(1)
	into STRICT	qt_quimio_w
	from  	pls_guia_plano_mat
	where  	nr_seq_guia = nr_seq_guia_p
	and  	ie_tipo_anexo = 'QU';

	select  count(1)
	into STRICT	qt_radio_w
	from  	pls_guia_plano_proc
	where  	nr_seq_guia = nr_seq_guia_p
	and  	ie_tipo_anexo = 'RA';

	if (qt_opme_w > 0) then
		select	count(1)
		into STRICT	qt_opme_ww
		from	pls_lote_anexo_guias_aut a,
			pls_lote_anexo_mat_aut	 b
		where  	b.nr_seq_lote_anexo_guia = a.nr_sequencia
		and	a.nr_seq_guia = nr_seq_guia_p
		and	ie_tipo_anexo = 'OP';
	end if;

	if (qt_quimio_w > 0) then
		select	count(1)
		into STRICT	qt_quimio_ww
		from	pls_lote_anexo_guias_aut a,
			pls_lote_anexo_mat_aut	 b
		where  	b.nr_seq_lote_anexo_guia = a.nr_sequencia
		and  	nr_seq_guia = nr_seq_guia_p
		and	ie_tipo_anexo = 'QU';
	end if;

	if (qt_radio_w > 0) then
		select	count(1)
		into STRICT	qt_radio_ww
		from	pls_lote_anexo_guias_aut
		where  	nr_seq_guia = nr_seq_guia_p
		and	ie_tipo_anexo = 'RA';
	end if;

	select	count(1)
	into STRICT	qt_consist_w
	from	ptu_consist_transacao
	where	nr_seq_guia = nr_seq_guia_p
	and	ie_habilita = 'S';

	if (qt_consist_w > 0) then
		update	ptu_consist_transacao
		set	ie_habilita = 'N'
		where	nr_seq_guia = nr_seq_guia_p;
	end if;

elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '')	then

	select	nr_seq_prestador_exec, 	--nr_seq_prestador_exec_w/nr_seq_prest_alto_custo_w
		nr_seq_clinica,
		ie_recem_nascido,
		dt_internacao_sugerida,
		ie_tipo_guia,
		nr_seq_prestador,	--nr_seq_prestador_req_w
		ie_origem_solic,
		ds_indicacao_clinica,
		ie_carater_atendimento
	into STRICT	nr_seq_prestador_exec_w,
		nr_seq_clinica_w,
		ie_recem_nascido_w,
		dt_internacao_sugerida_w,
		ie_tipo_guia_w,
		nr_seq_prestador_web_w,
		ie_origem_solic_w,
		ds_indicacao_clinica_w,
		ie_carater_atend_w
	from	pls_requisicao
	where	nr_sequencia = nr_seq_requisicao_p;

	begin
	    select  coalesce(ie_tipo_rede_min_ptu,1)
	    into STRICT    ie_tipo_rede_min_w
	    from    pls_prestador
	    where   nr_sequencia    = coalesce(nr_seq_prestador_exec_w,nr_seq_prestador_web_w);
	exception
	when others then
	    ie_tipo_rede_min_w := '1';
	end;

	select	max(cd_cgc_outorgante)
	into STRICT	cd_cgc_outorgante_w
	from	pls_outorgante;

	select	max(cd_cooperativa)
	into STRICT	cd_cooperativa_w
	from	pls_congenere
	where	cd_cgc			= cd_cgc_outorgante_w
	and	(cd_cooperativa IS NOT NULL AND cd_cooperativa::text <> '');

	begin
		select	substr(coalesce(cd_prestador,nr_sequencia),1,8)
		into STRICT	nr_seq_prestador_req_w
		from	pls_prestador
		where	nr_sequencia	= nr_seq_prestador_web_w;
	exception
	when others then
		nr_seq_prestador_req_w	:= nr_seq_prestador_web_w;
	end;

	if (nr_seq_prestador_req_w IS NOT NULL AND nr_seq_prestador_req_w::text <> '') then
		cd_unimed_prestador_req_w	:=	cd_cooperativa_w;
	end if;

	begin
		select	substr(coalesce(cd_prestador,nr_sequencia),1,8)
		into STRICT	nr_seq_prest_alto_custo_w
		from	pls_prestador
		where	nr_sequencia	= nr_seq_prestador_exec_w;
	exception
	when others then
		nr_seq_prest_alto_custo_w	:= nr_seq_prestador_exec_w;
	end;

	if (nr_seq_prest_alto_custo_w IS NOT NULL AND nr_seq_prest_alto_custo_w::text <> '') then
		cd_unimed_prestador_w		:=	cd_cooperativa_w;
		nm_prestador_alto_custo_w   	:= substr(pls_obter_dados_prestador(nr_seq_prestador_exec_w,'N'),1,25);
	end if;

	if (ie_carater_atend_w	= 'U') then
		ie_urg_emerg_w	:= 'S';
	else
		ie_urg_emerg_w	:= 'N';
	end if;

	ie_liminar_w := 'N';

	cd_estabelecimento_w := ptu_obter_estab_padrao;

	select	max(cd_municipio_ibge)
	into STRICT	cd_municipio_ibge_w
	from	pessoa_juridica
	where	cd_cgc	= (	SELECT	cd_cgc_outorgante
				from	pls_outorgante
				where	cd_estabelecimento = cd_estabelecimento_w);

	select	pls_obter_versao_tiss
	into STRICT	nr_versao_tiss_w
	;

	select  count(1)
	into STRICT	qt_opme_w
	from  	pls_requisicao_mat
	where  	nr_seq_requisicao = nr_seq_requisicao_p
	and  	ie_tipo_anexo = 'OP';

	select  count(1)
	into STRICT	qt_quimio_w
	from  	pls_requisicao_mat
	where  	nr_seq_requisicao = nr_seq_requisicao_p
	and  	ie_tipo_anexo = 'QU';

	select  count(1)
	into STRICT	qt_radio_w
	from  	pls_requisicao_proc
	where  	nr_seq_requisicao = nr_seq_requisicao_p
	and  	ie_tipo_anexo = 'RA';

	if (qt_opme_w > 0) then
		select	count(1)
		into STRICT	qt_opme_ww
		from	pls_lote_anexo_guias_aut a,
			pls_lote_anexo_mat_aut	 b
		where  	b.nr_seq_lote_anexo_guia = a.nr_sequencia
		and	nr_seq_requisicao = nr_seq_requisicao_p
		and	ie_tipo_anexo = 'OP';
	end if;

	if (qt_quimio_w > 0) then
		select	count(1)
		into STRICT	qt_quimio_ww
		from	pls_lote_anexo_guias_aut a,
			pls_lote_anexo_mat_aut	 b
		where  	b.nr_seq_lote_anexo_guia = a.nr_sequencia
		and	nr_seq_requisicao = nr_seq_requisicao_p
		and	ie_tipo_anexo = 'QU';
	end if;

	if (qt_radio_w > 0) then
		select	count(1)
		into STRICT	qt_radio_ww
		from	pls_lote_anexo_guias_aut
		where	nr_seq_requisicao = nr_seq_requisicao_p
		and	ie_tipo_anexo = 'RA';
	end if;

	select	count(1)
	into STRICT	qt_consist_w
	from	ptu_consist_transacao
	where	nr_seq_requisicao = nr_seq_requisicao_p
	and	ie_habilita = 'S';

	if (qt_consist_w > 0) then
		update	ptu_consist_transacao
		set	ie_habilita = 'N'
		where	nr_seq_requisicao = nr_seq_requisicao_p;
	end if;

end if;

for r_C01_w in C01 loop
	if (r_C01_w.cd_consistencia = '1') then
		if (coalesce(ie_tipo_rede_min_w::text, '') = '') then
			select	count(1)
			into STRICT	qt_prestador_w
			from	ptu_excecao_consistencia
			where	nr_seq_consist_envio = r_C01_w.nr_sequencia
			and	nr_seq_prestador = nr_seq_prestador_web_w;

			if (qt_prestador_w = 0) then
				if (ie_origem_solic_w <> 'P') or (ie_origem_solic_w = 'P' AND r_C01_w.ie_bloqueia_portal = 'S') then
					insert	into	ptu_consist_transacao(
							nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
							nm_usuario, nm_usuario_nrec, nr_seq_consist_envio,
							nr_seq_ordem_serv, nr_seq_pedido_aut, nr_seq_pedido_compl,
							nr_seq_requisicao, nr_seq_guia, ie_habilita
						)
					values (
							nextval('ptu_consist_transacao_seq'), clock_timestamp(), clock_timestamp(),
							nm_usuario_p, nm_usuario_p, r_C01_w.nr_sequencia,
							null,nr_seq_pedido_aut_p, null,
							nr_seq_requisicao_p, nr_seq_guia_p, 'S'
						);
				end if;
			end if;
		end if;
	elsif (r_C01_w.cd_consistencia = '2') then
		if (coalesce(cd_unimed_prestador_req_w::text, '') = '') and (nr_seq_prestador_req_w IS NOT NULL AND nr_seq_prestador_req_w::text <> '') then
			select	count(1)
			into STRICT	qt_prestador_w
			from	ptu_excecao_consistencia
			where	nr_seq_consist_envio = r_C01_w.nr_sequencia
			and	nr_seq_prestador = nr_seq_prestador_web_w;

			if (qt_prestador_w = 0) then
				if (ie_origem_solic_w <> 'P') or (ie_origem_solic_w = 'P' AND r_C01_w.ie_bloqueia_portal = 'S') then
					insert	into	ptu_consist_transacao(
							nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
							nm_usuario, nm_usuario_nrec, nr_seq_consist_envio,
							nr_seq_ordem_serv, nr_seq_pedido_aut, nr_seq_pedido_compl,
							nr_seq_requisicao, nr_seq_guia, ie_habilita
						)
					values (
							nextval('ptu_consist_transacao_seq'), clock_timestamp(), clock_timestamp(),
							nm_usuario_p, nm_usuario_p, r_C01_w.nr_sequencia,
							null,nr_seq_pedido_aut_p, null,
							nr_seq_requisicao_p, nr_seq_guia_p, 'S'
						);
				end if;
			end if;
		end if;
	elsif (r_C01_w.cd_consistencia = '3') then
		if (coalesce(nr_seq_prestador_req_w::text, '') = '') and (cd_unimed_prestador_req_w IS NOT NULL AND cd_unimed_prestador_req_w::text <> '') then
			select	count(1)
			into STRICT	qt_prestador_w
			from	ptu_excecao_consistencia
			where	nr_seq_consist_envio = r_C01_w.nr_sequencia
			and	nr_seq_prestador = nr_seq_prestador_web_w;

			if (qt_prestador_w = 0) then
				if (ie_origem_solic_w <> 'P') or (ie_origem_solic_w = 'P' AND r_C01_w.ie_bloqueia_portal = 'S') then
					insert	into	ptu_consist_transacao(
							nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
							nm_usuario, nm_usuario_nrec, nr_seq_consist_envio,
							nr_seq_ordem_serv, nr_seq_pedido_aut, nr_seq_pedido_compl,
							nr_seq_requisicao, nr_seq_guia, ie_habilita
