SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_copiar_grupo_serv_versao ( nr_seq_versao_p bigint, nr_seq_versao_copia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE

qt_regra_versao_p	bigint;

BEGIN

select	count(1)
into STRICT	qt_regra_versao_p
from	ptu_versao_tabela
where	nr_seq_versao = nr_seq_versao_copia_p  LIMIT 1;

if (qt_regra_versao_p > 0) then
	insert into	ptu_versao_tabela( 	dt_atualizacao, dt_atualizacao_nrec, ie_situacao,
						nm_usuario, nm_usuario_nrec, nr_seq_grupo_servico,
						nr_sequencia, nr_seq_versao)
					SELECT	clock_timestamp(), clock_timestamp(), a.ie_situacao,
						nm_usuario_p, nm_usuario_p, a.nr_seq_grupo_servico,
						nextval('ptu_versao_tabela_seq'), nr_seq_versao_p
					from	ptu_versao_tabela	a
					where	nr_seq_versao = nr_seq_versao_copia_p
					and		not exists (	SELECT 1
										from	ptu_versao_tabela x
										where	x.nr_seq_grupo_servico = a.nr_seq_grupo_servico
										and	x.nr_seq_versao = nr_seq_versao_p);
	ds_retorno_p := '';
else
	ds_retorno_p := 'Não existem grupos de serviço cadastrados para a versão informada! ';
end if;



commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
