SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_criar_job_proces_imp_a500 (nr_seq_fatura_p ptu_fatura.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


jobno			bigint;
ds_comando_job_w	varchar(2000);

C01 CURSOR FOR
	-- falhas is not null significa que já foi executado pelo menos uma vez
	-- se já foi executado ao menos uma vez, apaga.
	-- durante a execução ele fica null
	SELECT	job
	from	job_v
	where	comando	like 'ptu_processo_a500_job%'
	and	(falhas IS NOT NULL AND falhas::text <> '');

BEGIN

-- Verificar se a job já existe, se existir remove
for r_C01_w in C01 loop
	dbms_job.remove(r_C01_w.job);
	commit;
end loop;

ds_comando_job_w	:= 'ptu_processo_a500_job(' || 	nr_seq_fatura_p || ',' ||
							pls_util_pck.aspas_w ||  nm_usuario_p || pls_util_pck.aspas_w || ',' ||
							coalesce(to_char(cd_estabelecimento_p), 'null') ||  ');';

--Vai iniciar a procedure em 10 segundos após a conclusão desta
-- e sempre joga a próxima execução para daqui a 10000 dias
dbms_job.submit(jobno, ds_comando_job_w, clock_timestamp() + interval '5 days' * (1/24/60/60), 'sysdate + 10000');

-- coloca o id do job responsável por executar o processo.
update	ptu_fatura
set	nm_id_job 	= jobno
where	nr_sequencia 	= nr_seq_fatura_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
