SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_desfazer_geracao_tit_a500 ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE

					 
nr_fatura_w		bigint;
nr_sequencia_w		bigint;
nr_fatura_ww		ptu_fatura.nr_fatura%type;

c01 CURSOR FOR 
SELECT	a.nr_sequencia, 
	a.nr_fatura 
from	ptu_fatura a 
where	a.nr_seq_lote	= nr_seq_lote_p 
and	(a.nr_fatura IS NOT NULL AND a.nr_fatura::text <> '');				
 

BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_sequencia_w, 
	nr_fatura_ww;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	nr_fatura_w	:= somente_numero(nr_fatura_ww);
	CALL cancelar_titulo_receber(nr_fatura_w,nm_usuario_p,'N',clock_timestamp());
	 
	update	titulo_receber 
	set	nr_seq_ptu_fatura	 = NULL 
	where	nr_titulo		= nr_fatura_w;
	 
	update	ptu_fatura 
	set	nr_fatura	 = NULL, 
		nm_usuario	= nm_usuario_p, 
		dt_atualizacao	= clock_timestamp() 
	where	nr_sequencia	= nr_sequencia_w;
	end;
end loop;
close c01;
 
update	ptu_lote_fatura_envio 
set	dt_geracao_titulo	 = NULL, 
	nm_usuario	= nm_usuario_p, 
	dt_atualizacao	= clock_timestamp() 
where	nr_sequencia	= nr_seq_lote_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
