CREATE OR REPLACE PROCEDURE ptu_desfazer_inconsist_benef ( nr_seq_intercambio_p bigint, cd_estabelecimento bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_beneficiario_w	bigint;

C01 CURSOR FOR
	SELECT	b.nr_sequencia
	from	ptu_intercambio_benef	b,
		ptu_intercambio_empresa	a
	where	b.nr_seq_empresa	= a.nr_sequencia
	and	a.nr_seq_intercambio	= nr_seq_intercambio_p
	and	b.ie_status		= 'S'
	and	b.ie_status		<> 'C';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_beneficiario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	delete	FROM ptu_intercambio_consist
	where	nr_seq_inter_benef	 = nr_seq_beneficiario_w;

	update	ptu_intercambio_benef
	set	ie_status			= 'I'
	where	nr_sequencia	= nr_seq_beneficiario_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
