CREATE OR REPLACE PROCEDURE ptu_desfazer_lote_fatura_pre ( nr_seq_lote_p bigint) AS $body$
DECLARE


nr_seq_fatura_w		bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	ptu_fatura_pre
	where	nr_seq_lote	= nr_seq_lote_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_fatura_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	delete	FROM ptu_fatura_pre_corpo
	where	nr_seq_fatura	= nr_seq_fatura_w;

	delete	FROM ptu_fatura_pre_sacado
	where	nr_seq_fatura	= nr_seq_fatura_w;

	delete	FROM ptu_fatura_pre_total
	where	nr_seq_fatura	= nr_seq_fatura_w;

	delete	FROM ptu_fatura_pre_cobranca
	where	nr_seq_fatura	= nr_seq_fatura_w;

	delete	FROM ptu_fatura_pre
	where	nr_sequencia	= nr_seq_fatura_w;

	end;
end loop;
close c01;

update	ptu_fatura_pre_lote
set	dt_geracao	 = NULL,
	DT_GERACAO_ARQUIVO	 = NULL
where	nr_sequencia	= nr_seq_lote_p;

commit;

end;
$body$
