SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_desfazer_lote_imp_a700 ( nr_seq_serv_pre_pgto_p ptu_servico_pre_pagto.nr_sequencia%type, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Desfazer lote importado do A700
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_nota_cobr_w			ptu_nota_cobranca.nr_sequencia%type;
nr_seq_nota_hospitalar_w		ptu_nota_hospitalar.nr_sequencia%type;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	ptu_nota_cobranca
	where	nr_seq_serv_pre_pagto = nr_seq_serv_pre_pgto_p;

c02 CURSOR FOR
	SELECT	nr_sequencia
	from	ptu_nota_hospitalar
	where	nr_seq_nota_cobr	= nr_seq_nota_cobr_w;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_nota_cobr_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	delete	FROM ptu_serv_pre_pagto_incons
	where	nr_seq_nota_cobr	= nr_seq_nota_cobr_w;

	open c02;
	loop
	fetch c02 into
		nr_seq_nota_hospitalar_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin

		delete	FROM ptu_nota_hosp_compl
		where	nr_seq_nota_hosp	= nr_seq_nota_hospitalar_w;

		end;
	end loop;
	close c02;

	update	pls_conta
	set	NR_SEQ_NOTA_COBRANCA	 = NULL,
		NR_SEQ_NOTA_HOSPITALAR	 = NULL
	where	NR_SEQ_NOTA_COBRANCA	= nr_seq_nota_cobr_w;

	delete	FROM ptu_nota_hospitalar
	where	nr_seq_nota_cobr	= nr_seq_nota_cobr_w;

	delete	FROM ptu_nota_servico
	where	nr_seq_nota_cobr	= nr_seq_nota_cobr_w;

	delete	FROM ptu_nota_complemento
	where	nr_seq_nota_cobr	= nr_seq_nota_cobr_w;

	delete	FROM ptu_nota_fiscal
	where	nr_seq_nota_cobr	= nr_seq_nota_cobr_w;

	delete	FROM ptu_nota_cobranca
	where	nr_sequencia		= nr_seq_nota_cobr_w;

	end;
end loop;
close c01;

update	pls_conta
set	NR_SEQ_SERV_PRE_PAGTO	 = NULL
where	NR_SEQ_SERV_PRE_PAGTO	= nr_seq_serv_pre_pgto_p;

update	pls_protocolo_conta
set	NR_SEQ_SERV_PRE_PAGTO	 = NULL
where	NR_SEQ_SERV_PRE_PAGTO	= nr_seq_serv_pre_pgto_p;

delete	FROM PTU_SERVICO_PRE_TOTAL
where	NR_SEQ_SERV_PRE_PAGTO	= nr_seq_serv_pre_pgto_p;

delete	FROM ptu_servico_pre_pagto
where	nr_sequencia	= nr_seq_serv_pre_pgto_p;

if (coalesce(ie_commit_p,'N') = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
