SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_documento_fisico_recebido ( nr_seq_conta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_analise_conta_item_w	bigint;
nr_seq_conta_proc_w		bigint;
nr_seq_conta_mat_w		bigint;
nr_seq_analise_w		bigint;
nr_seq_glosa_oc_w		bigint;
ie_tipo_w			varchar(10);
nr_seq_mot_liberacao_w		bigint;
nr_seq_grupo_pre_analise_w	bigint;
nr_seq_ocor_benef_w		bigint;
nr_seq_mot_liberacao_glosa_w	pls_param_analise_conta.nr_seq_mot_liberacao_glosa%type;

C01 CURSOR FOR 
	SELECT	b.nr_sequencia, 
		a.nr_seq_analise 
	from	pls_analise_glo_ocor_grupo	a, 
		pls_ocorrencia_benef		b 
	where	a.ie_status		in ('P') 
	and	b.nr_sequencia		= a.nr_seq_ocor_benef 
	and	b.nr_seq_conta		= nr_seq_conta_p 
	and	((a.nr_seq_grupo = nr_seq_grupo_pre_analise_w) or (coalesce(nr_seq_grupo_pre_analise_w::text, '') = '')) 
	and	exists (SELECT	1 
			from	pls_ocorrencia	x 
			where	coalesce(x.ie_documento_fisico, 'N')	= 'S' 
			and	x.nr_sequencia			= b.nr_seq_ocorrencia 
			and	x.ie_situacao			= 'A');

BEGIN
 
begin 
select	a.nr_seq_grupo_pre_analise 
into STRICT	nr_seq_grupo_pre_analise_w 
from	pls_parametros	a 
where	a.cd_estabelecimento	= cd_estabelecimento_p;
exception 
	when others then 
	nr_seq_grupo_pre_analise_w	:= null;
end;
begin 
	select	nr_seq_mot_liberacao_glosa 
	into STRICT	nr_seq_mot_liberacao_glosa_w 
	from	pls_param_analise_conta a 
	where	a.cd_estabelecimento	= cd_estabelecimento_p;
exception 
when others then 
	nr_seq_mot_liberacao_glosa_w	:= null;
end;
 
open C01;
loop 
fetch C01 into	 
	nr_seq_ocor_benef_w, 
	nr_seq_analise_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_mot_liberacao_w 
	from	pls_mot_lib_analise_conta 
	where	ie_situacao			= 'A' 
	and	coalesce(ie_documento_fisico,'N')	= 'S';
	 
	/*if	(nr_seq_mot_liberacao_w is not null) then 
		pls_conta_liberar_glosa_oc(	nr_seq_analise_conta_item_w, nr_seq_analise_w, nr_seq_mot_liberacao_w, 
						'', nm_usuario_p, cd_estabelecimento_p, null, 
						'N', 'N','N'); 
	else 
		--Não existe motivo liberação de documento físico recebido,(Cadastros Gerais / Plano de Saúde / OPS - Contas médicas / Motivos de liberação da análise da conta).		 
		wheb_mensagem_pck.exibir_mensagem_abort(177515); 
	end if;*/
 
 
	update	pls_ocorrencia_benef 
	set	ie_situacao	= 'I', 
		ie_forma_inativacao	= CASE WHEN ie_forma_inativacao='S' THEN 'US' WHEN ie_forma_inativacao='US' THEN 'US'  ELSE 'U' END , 
		dt_atualizacao = clock_timestamp(), 
		nm_usuario	= nm_usuario_p 
	where	nr_sequencia	= nr_seq_ocor_benef_w;
	 
	update	pls_conta_glosa 
	set	ie_situacao = 'I', 
		ie_forma_inativacao	= CASE WHEN ie_forma_inativacao='S' THEN 'US' WHEN ie_forma_inativacao='US' THEN 'US'  ELSE 'U' END , 
		dt_atualizacao = clock_timestamp(), 
		nm_usuario	= nm_usuario_p 
	where	nr_seq_ocorrencia_benef = nr_seq_ocor_benef_w;
	 
	CALL pls_propagar_lib_conta(	nr_seq_analise_w,  
				nr_seq_conta_p, 
				nr_seq_grupo_pre_analise_w, 
				nr_seq_mot_liberacao_glosa_w, 
				cd_estabelecimento_p, 
				nm_usuario_p);
	 
	update	pls_analise_glo_ocor_grupo a 
	set	ie_status	= 'L', 
		nm_usuario	= nm_usuario_p, 
		dt_atualizacao	= clock_timestamp() 
	where	a.nr_seq_ocor_benef	= nr_seq_ocor_benef_w 
	and	a.nr_seq_grupo		= nr_seq_grupo_pre_analise_w;
	 
	end;
end loop;
close C01;
 
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ptu_documento_fisico_recebido ( nr_seq_conta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

