CREATE OR REPLACE PROCEDURE ptu_excluir_fatura_geral_rec ( nr_seq_fat_geral_p ptu_fatura_geral.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


qt_fat_geral_anexo			integer;
qt_fat_geral_hist			integer;


BEGIN

select  count(1)
into STRICT    qt_fat_geral_anexo
from    ptu_fatura_geral_anexo
where   nr_seq_fat_geral = nr_seq_fat_geral_p;

select  count(1)
into STRICT    qt_fat_geral_hist
from    ptu_fatura_geral_hist
where   nr_seq_fat_geral = nr_seq_fat_geral_p;

if (qt_fat_geral_anexo = 1 or qt_fat_geral_hist = 1) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(402684);
end if;

delete	FROM ptu_fatura_geral
where	nr_sequencia	= nr_seq_fat_geral_p
and	coalesce(dt_postagem::text, '') = '';

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
