CREATE OR REPLACE PROCEDURE ptu_gerar_anexo_guia_req_compl ( nr_seq_pedido_aut_compl_p ptu_pedido_compl_aut.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

 
C01 CURSOR( nr_seq_pedido_aut_compl_pc 	ptu_pedido_compl_aut.nr_sequencia%type ) FOR 
	SELECT	a.nr_seq_guia, 
		a.nr_seq_requisicao, 
		a.ds_biometria 
	from	ptu_pedido_compl_aut a 
	where	a.nr_sequencia = nr_seq_pedido_aut_compl_pc;

C02 CURSOR( nr_seq_pedido_aut_compl_pc 	ptu_pedido_compl_aut.nr_sequencia%type ) FOR 
	SELECT	c.nr_sequencia 
	from	ptu_pedido_aut_anexo c 
	where	c.nr_seq_pedido_compl = nr_seq_pedido_aut_compl_pc;	
 
BEGIN 
 
--Carrega os itens importados no pedido de autorização 
for c01_w in C01( nr_seq_pedido_aut_compl_p ) loop 
	for c02_w in C02( nr_seq_pedido_aut_compl_p ) loop 
		if	(c01_w.nr_seq_guia IS NOT NULL AND c01_w.nr_seq_guia::text <> '' AND c02_w.nr_sequencia IS NOT NULL AND c02_w.nr_sequencia::text <> '') then 
			CALL ptu_inserir_dados_anexo_guia( c01_w.nr_seq_guia, c02_w.nr_sequencia, c01_w.ds_biometria, nm_usuario_p);
		elsif	(c01_w.nr_seq_requisicao IS NOT NULL AND c01_w.nr_seq_requisicao::text <> '' AND c02_w.nr_sequencia IS NOT NULL AND c02_w.nr_sequencia::text <> '') then 
			CALL ptu_inserir_dados_anexo_req( c01_w.nr_seq_requisicao, c02_w.nr_sequencia, c01_w.ds_biometria, nm_usuario_p);
		end if;
	end loop;
end loop;
 
end;
$body$
