CREATE OR REPLACE PROCEDURE ptu_gerar_auditoria_guia ( nr_seq_guia_p bigint, nr_seq_complemento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_guia_proc_w		bigint;
nr_seq_guia_mat_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_guia_plano_proc
	where	nr_seq_guia	= nr_seq_guia_p
	and	ie_status	in ('N','M','K');

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_guia_plano_mat
	where	nr_seq_guia	= nr_seq_guia_p
	and	ie_status	in ('N','M','K');


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_guia_proc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (coalesce(nr_seq_complemento_p::text, '') = '') then
		update	pls_guia_plano_proc
		set	ie_status	= 'A'
		where	nr_sequencia	= nr_seq_guia_proc_w;
	elsif (nr_seq_complemento_p IS NOT NULL AND nr_seq_complemento_p::text <> '') then
		update	pls_guia_plano_proc
		set	ie_status		= 'A'
		where	nr_sequencia		= nr_seq_guia_proc_w
		and	nr_seq_compl_ptu	= nr_seq_complemento_p;
	end if;

	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_seq_guia_mat_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin

	if (coalesce(nr_seq_complemento_p::text, '') = '') then
		update	pls_guia_plano_mat
		set	ie_status	= 'A'
		where	nr_sequencia	= nr_seq_guia_mat_w;
	elsif (nr_seq_complemento_p IS NOT NULL AND nr_seq_complemento_p::text <> '') then
		update	pls_guia_plano_mat
		set	ie_status		= 'A'
		where	nr_sequencia		= nr_seq_guia_mat_w
		and	nr_seq_compl_ptu	= nr_seq_complemento_p;
	end if;

	end;
end loop;
close C02;


--if	(nr_seq_complemento_p	is null) then
	update	pls_guia_plano
	set	ie_estagio	= 1
	where	nr_sequencia	= nr_seq_guia_p;
--end if;
commit;

end;
$body$
