CREATE OR REPLACE PROCEDURE ptu_gerar_auditoria_req ( nr_seq_requisicao_p bigint, nr_seq_complemento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


