CREATE OR REPLACE PROCEDURE ptu_gerar_benef_plano_agregado ( nr_seq_segurado_p bigint, nr_seq_beneficiario_p bigint, dt_mov_final_p timestamp, nm_usuario_p text) AS $body$
DECLARE


cd_tipo_produto_w		varchar(2);
nm_produto_w			varchar(20);

C01 CURSOR FOR
	SELECT	substr(obter_valor_dominio(3205,d.ie_tipo_classificacao),1,20),
		d.ie_tipo_classificacao
	from	pls_segurado		a,
		pls_plano		b,
		pls_sca_vinculo		c,
		pls_sca_classificacao	d
	where	c.nr_seq_plano		= b.nr_sequencia
	and	a.nr_sequencia		= c.nr_seq_segurado
	and	b.nr_seq_classificacao	= d.nr_sequencia
	and	a.nr_sequencia		= nr_seq_segurado_p
	and (coalesce(c.dt_fim_vigencia::text, '') = '' or (c.dt_fim_vigencia > coalesce(dt_mov_final_p, clock_timestamp())));


BEGIN

open C01;
loop
fetch C01 into
	nm_produto_w,
	cd_tipo_produto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	if (cd_tipo_produto_w IS NOT NULL AND cd_tipo_produto_w::text <> '') then
		nm_produto_w	:= elimina_acentos(nm_produto_w);
		nm_produto_w	:= replace(replace(replace(nm_produto_w,'รง','c'),'/',' '),'-',' ');
		
		insert into ptu_benef_plano_agregado(	nr_sequencia,dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
				nr_seq_beneficiario, cd_tipo_produto,ds_produto)
		values (	nextval('ptu_benef_plano_agregado_seq'),clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
				nr_seq_beneficiario_p, cd_tipo_produto_w,nm_produto_w);
	end if;
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
