SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_gerar_camara_comp_contas ( nr_seq_lote_fatura_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_ptu_camara_p INOUT bigint) AS $body$
DECLARE


nr_seq_ptu_camara_w	bigint;
dt_saldo_credor_w	timestamp;
cd_unimed_origem_w	varchar(20);
cd_unimed_destino_w	varchar(20);
dt_lote_w		timestamp;
ie_tipo_camara_w	varchar(5);
nr_versao_transacao_w	smallint;
nr_fatura_w		bigint;
ie_tipo_fatura_w	varchar(5);
cd_unimed_devedora_w	varchar(20);
vl_total_fatura_w	double precision;
nr_seq_camara_w		bigint;
nr_seq_periodo_w	bigint;
nr_fatura_ww		ptu_fatura.nr_fatura%type;

C01 CURSOR FOR
	SELECT	a.nr_fatura,
		b.vl_titulo
	from	ptu_lote_fatura_envio	c,
		titulo_receber		b,
		ptu_fatura		a
	where	a.nr_fatura	= b.nr_titulo
	and	a.nr_seq_lote	= c.nr_sequencia
	and	c.nr_sequencia	= nr_seq_lote_fatura_p;


BEGIN

if (nr_seq_lote_fatura_p IS NOT NULL AND nr_seq_lote_fatura_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then

	select	nextval('ptu_camara_compensacao_seq')
	into STRICT	nr_seq_ptu_camara_w
	;

	select	pls_obter_unimed_estab(cd_estabelecimento_p)
	into STRICT	cd_unimed_origem_w
	;

	select	a.dt_geracao_lote,
		a.nr_seq_camara,
		a.nr_seq_periodo
	into STRICT	dt_lote_w,
		nr_seq_camara_w,
		nr_seq_periodo_w
	from	ptu_lote_fatura_envio a
	where	a.nr_sequencia	= nr_seq_lote_fatura_p;

	select	coalesce(max(a.dt_saldo_credor),clock_timestamp())
	into STRICT	dt_saldo_credor_w
	from	pls_camara_calend_periodo a
	where	a.nr_sequencia	= nr_seq_periodo_w;

	/* Unimed destino precisa obter  unimed responsável pela câmara */

	begin
	select	b.cd_cooperativa,
		a.ie_tipo_camara
	into STRICT	cd_unimed_destino_w,
		ie_tipo_camara_w
	from	pls_congenere b,
		pls_camara_compensacao a
	where	a.nr_seq_coop_resp	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_camara_w;
	exception
	when others then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(266995);
	end;

	insert into ptu_camara_compensacao(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		dt_geracao,
		dt_camara,
		ie_tipo_camara,
		nr_versao_transacao,
		nr_seq_lote_camara,
		cd_unimed_origem,
		cd_unimed_destino)
	values (nr_seq_ptu_camara_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		dt_lote_w,
		dt_saldo_credor_w,
		ie_tipo_camara_w,
		4,
		null,
		cd_unimed_origem_w,
		cd_unimed_destino_w);

	/* Gerar faturas */

	open C01;
	loop
	fetch C01 into
		nr_fatura_ww,
		vl_total_fatura_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		nr_fatura_w	:= somente_numero(nr_fatura_ww);
		/*(1,2,3,4,5,6)(Pré-Pagamento,Custo Operacional,Contestação,Outros,Fatura PCMSO,Recontestação)*/

		ie_tipo_fatura_w	:= 1;

		select	max(b.cd_cooperativa)
		into STRICT	cd_unimed_devedora_w
		from	pls_congenere b,
			titulo_receber a
		where	a.cd_cgc	= b.cd_cgc
		and	a.nr_titulo	= nr_fatura_w;

		insert into ptu_camara_fatura(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nr_seq_camara,
			ie_tipo_fatura,
			nr_fatura,
			cd_unimed_credora,
			cd_unimed_devedora,
			vl_total_fatura)
		values (nextval('ptu_camara_fatura_seq'),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_ptu_camara_w,
			ie_tipo_fatura_w,
			nr_fatura_w,
			cd_unimed_origem_w,
			cd_unimed_devedora_w,
			vl_total_fatura_w);
		end;
	end loop;
	close C01;

update	ptu_lote_fatura_envio
set	dt_geracao_a600	= clock_timestamp()
where	nr_sequencia	= nr_seq_lote_fatura_p;

nr_seq_ptu_camara_p	:= nr_seq_ptu_camara_w;

commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ptu_gerar_camara_comp_contas ( nr_seq_lote_fatura_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_ptu_camara_p INOUT bigint) FROM PUBLIC;

