CREATE OR REPLACE PROCEDURE ptu_gerar_erro_inesperado ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_unimed_exec_w		smallint;
cd_unimed_benef_w		smallint;
nr_seq_execucao_w		bigint;
nr_seq_origem_w			bigint;
ie_tipo_cliente_w		varchar(2);


BEGIN

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	delete	from ptu_erro_inesperado
	where	nr_seq_guia	= nr_seq_guia_p;

	select	cd_unimed_executora,
		cd_unimed_beneficiario,
		nr_seq_execucao,
		nr_seq_origem,
		ie_tipo_cliente
	into STRICT	cd_unimed_exec_w,
		cd_unimed_benef_w,
		nr_seq_execucao_w,
		nr_seq_origem_w,
		ie_tipo_cliente_w
	from	ptu_resposta_autorizacao
	where	nr_seq_guia	= nr_seq_guia_p;
elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	delete	from ptu_erro_inesperado
	where	nr_seq_requisicao	= nr_seq_requisicao_p;

	select	cd_unimed_executora,
		cd_unimed_beneficiario,
		nr_seq_execucao,
		nr_seq_origem,
		ie_tipo_cliente
	into STRICT	cd_unimed_exec_w,
		cd_unimed_benef_w,
		nr_seq_execucao_w,
		nr_seq_origem_w,
		ie_tipo_cliente_w
	from	ptu_resposta_autorizacao
	where	nr_seq_requisicao	= nr_seq_requisicao_p;
end if;

insert	into ptu_erro_inesperado(nr_sequencia, cd_transacao, ie_tipo_cliente,
	 cd_unimed_executora, cd_unimed_beneficiario, dt_atualizacao,
	 nm_usuario, nr_seq_execucao, nr_seq_origem,
	 nr_seq_requisicao, nr_seq_guia)
values (nextval('ptu_erro_inesperado_seq'), '00210', ie_tipo_cliente_w,
	 cd_unimed_exec_w, cd_unimed_benef_w, clock_timestamp(),
	 nm_usuario_p, nr_seq_execucao_w, nr_seq_origem_w,
	 nr_seq_requisicao_p, nr_seq_guia_p);

commit;

end;
