SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_gerar_fatura_geral_rec ( nr_titulo_receber_p titulo_receber.nr_titulo%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


cd_uni_des_w			ptu_fatura_geral.cd_uni_des%type;
cd_uni_ori_w			ptu_fatura_geral.cd_uni_ori%type;
dt_pagamento_previsto_w		titulo_receber.dt_pagamento_previsto%type;
dt_emissao_w			titulo_receber.dt_emissao%type;
vl_saldo_titulo_w		titulo_receber.vl_saldo_titulo%type;
qt_fat_geral_w			integer;
nr_seq_congenere_w              pls_congenere.nr_seq_congenere%type;
nr_versao_transacao_w           ptu_fatura_geral.nr_ver_tra%type;
cd_interface_w                  interface.cd_interface%type;


BEGIN
select	count(1)
into STRICT	qt_fat_geral_w
from	ptu_fatura_geral
where	nr_titulo_receber	= nr_titulo_receber_p;

if (nr_titulo_receber_p IS NOT NULL AND nr_titulo_receber_p::text <> '') and (qt_fat_geral_w = 0) then
	select	max(c.cd_cooperativa),
		max(x.dt_pagamento_previsto),
		max(x.dt_emissao),
		max(x.vl_saldo_titulo),
                max(c.nr_sequencia)
	into STRICT	cd_uni_des_w,
		dt_pagamento_previsto_w,
		dt_emissao_w,
		vl_saldo_titulo_w,
                nr_seq_congenere_w
	from	titulo_receber	x,
		pls_congenere	c
	where	c.cd_cgc	= x.cd_cgc
	and	x.nr_titulo	= nr_titulo_receber_p;
	

	select	max(c.cd_cooperativa)
	into STRICT	cd_uni_ori_w
	from	pls_outorgante	o,
		pls_congenere	c
	where	c.cd_cgc		= o.cd_cgc_outorgante
	and	o.cd_estabelecimento	= cd_estabelecimento_p;

	if (cd_uni_des_w IS NOT NULL AND cd_uni_des_w::text <> '') and (vl_saldo_titulo_w > 0) then

                cd_interface_w := pls_obter_interf_ptu(cd_estabelecimento_p, nr_seq_congenere_w, clock_timestamp(), 'A580');

                nr_versao_transacao_w := ptu_obter_versao_transacao('A580', ptu_obter_versao('A580', cd_interface_w));

		insert into ptu_fatura_geral(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_titulo_receber,
			nr_titulo_pagar,
			cd_uni_des,
			cd_uni_ori,
			dt_geracao,
			nr_comp,
			dt_ven_doc,
			dt_emi_doc,
			vl_tot_doc,
			nr_ver_tra,
			vl_ir,
			nr_documento,
			doc_fiscal,
			tp_documento,
			dt_postagem,
			nr_protocolo,
			id_cobranca)
		values (nextval('ptu_fatura_geral_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_titulo_receber_p,
			null,
			lpad(cd_uni_des_w,4,'0'),
			lpad(cd_uni_ori_w,4,'0'),
			clock_timestamp(),
			to_char(clock_timestamp(),'YYMM'),
			dt_pagamento_previsto_w,
			dt_emissao_w,
			vl_saldo_titulo_w,
			nr_versao_transacao_w,
			0,
			nr_titulo_receber_p,
			nr_titulo_receber_p,
			'1',
			null,
			null,
			-- Atualmente Ã© fixo o "[9] - Outros"
			'9');
		
		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
