CREATE OR REPLACE PROCEDURE ptu_gerar_fatura ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_fatura_w			bigint;
C01 CURSOR FOR
	SELECT	nr_sequencia 
	from	ptu_fatura 
	where	nr_seq_lote = nr_sequencia_p;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_fatura_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL ptu_gerar_nota_cobranca(nr_seq_fatura_w, nm_usuario_p);
	CALL ptu_gerar_fatura_cedente(nr_seq_fatura_w, nm_usuario_p);
	/*Robson OS - 291031 ultimo item do anexo (21) 
	ptu_gerar_fatura_corpo(nr_seq_fatura_w, nm_usuario_p);*/
 
	CALL ptu_gerar_fatura_boleto(nr_seq_fatura_w, nm_usuario_p);
	end;
end loop;
close C01;	
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
