SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_gerar_lote_conta_a700 ( nr_seq_servico_p ptu_servico_pre_pagto.nr_sequencia%type, nr_seq_protocolo_p INOUT pls_protocolo_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_protocolo_w    		pls_protocolo_conta.nr_sequencia%type;
nr_seq_outorgante_w    		pls_outorgante.nr_sequencia%type;
cd_estabelecimento_w    	estabelecimento.cd_estabelecimento%type;
cd_unimed_destino_w    		ptu_servico_pre_pagto.cd_unimed_destino%type;
cd_unimed_origem_w    		ptu_servico_pre_pagto.cd_unimed_origem%type;
nr_seq_congenere_w    		pls_congenere.nr_sequencia%type;
nr_seq_lote_protocolo_w    	pls_lote_protocolo_conta.nr_sequencia%type;
dt_fim_pgto_w     	 	ptu_servico_pre_pagto.dt_fim_pagto%type;
dt_geracao_w      		ptu_servico_pre_pagto.dt_geracao%type;
dt_mes_competencia_w		pls_protocolo_conta.dt_mes_competencia%type;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_protocolo_w
from	pls_protocolo_conta
where	nr_seq_serv_pre_pagto	= nr_seq_servico_p;

/* Verificar se já não tem protocolo gerado para a fatura */

if (nr_seq_protocolo_w is  null) then

	select	cd_estabelecimento,
		cd_unimed_destino,
		cd_unimed_origem,
		dt_fim_pagto,
		dt_geracao
	into STRICT	cd_estabelecimento_w,
		cd_unimed_destino_w,
		cd_unimed_origem_w,
		dt_fim_pgto_w,
		dt_geracao_w
	from	ptu_servico_pre_pagto
	where	nr_sequencia	= nr_seq_servico_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_outorgante_w
	from	pls_outorgante;

	select	nextval('pls_protocolo_conta_seq')
	into STRICT	nr_seq_protocolo_w
	;

	begin
		select	nr_sequencia
		into STRICT	nr_seq_congenere_w
		from	pls_congenere
		where	somente_numero(cd_cooperativa)	= somente_numero(cd_unimed_origem_w);
	exception
	when others then
		nr_seq_congenere_w	:= null;
	end;

	/*Gerar o Lotes de Protocolo com os protocolos gerados. Todos os protocolos gerados na mesma importação seram particioantes do mesmo lote*/

	select	nextval('pls_lote_protocolo_conta_seq')
	into STRICT	nr_seq_lote_protocolo_w
	;

	insert	into pls_lote_protocolo_conta(nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
		 nm_usuario, nm_usuario_nrec, dt_lote,
		 nr_seq_congenere, ie_tipo_lote, cd_estabelecimento,
		 ie_origem_analise, ie_status)
	values (nr_seq_lote_protocolo_w, clock_timestamp(), clock_timestamp(),
		 nm_usuario_p, nm_usuario_p, clock_timestamp(),
		 nr_seq_congenere_w, 'I', cd_estabelecimento_p,
		 6, 'U');

	dt_mes_competencia_w	:= pls_obter_dataref_prot_imp(null, 'Z',clock_timestamp(),
								clock_timestamp(),clock_timestamp(),clock_timestamp(),
								null,null, cd_estabelecimento_p);
	insert into pls_protocolo_conta(nr_sequencia, dt_atualizacao, nm_usuario,
		dt_atualizacao_nrec, nm_usuario_nrec, dt_protocolo,
		dt_mes_competencia, nr_protocolo_prestador, nr_seq_prestador,
		nr_seq_outorgante, ie_apresentacao,
		ds_observacao, dt_integracao, nm_usuario_integracao,
		ie_status, ie_situacao, ie_tipo_protocolo,
		cd_estabelecimento, dt_base_venc, nr_seq_congenere,
		nr_seq_lote_conta, ie_origem_protocolo, dt_recebimento,
		nr_seq_serv_pre_pagto)
	values (nr_seq_protocolo_w, clock_timestamp(), nm_usuario_p,
		clock_timestamp(), nm_usuario_p, clock_timestamp(),
		clock_timestamp(), null, null,
		nr_seq_outorgante_w, 'A',
		null, dt_fim_pgto_w, '',
		'7', 'T', 'F',
		cd_estabelecimento_p, clock_timestamp(), nr_seq_congenere_w,
		nr_seq_lote_protocolo_w, 'Z', clock_timestamp(),
		nr_seq_servico_p);
end if;

nr_seq_protocolo_p	:= nr_seq_protocolo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ptu_gerar_lote_conta_a700 ( nr_seq_servico_p ptu_servico_pre_pagto.nr_sequencia%type, nr_seq_protocolo_p INOUT pls_protocolo_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;

