CREATE OR REPLACE PROCEDURE ptu_gerar_lote_envio_js ( nr_seq_lote_p bigint, nm_usuario_p text, ds_mensagem_p INOUT text) AS $body$
DECLARE

 
qt_inconsistente_w	bigint;


BEGIN 
ds_mensagem_p := '';
CALL ptu_gerar_lote_envio(nr_seq_lote_p, nm_usuario_p);
 
select	count(*) 
into STRICT	qt_inconsistente_w 
from	ptu_intercambio_consist	a, 
	ptu_intercambio_benef b, 
	ptu_intercambio_empresa c, 
	ptu_intercambio	d 
where	a.nr_seq_inter_benef = b.nr_sequencia 
and	b.nr_seq_empresa = c.nr_sequencia 
and	c.nr_seq_intercambio = d.nr_sequencia 
and	d.nr_seq_lote_envio = nr_seq_lote_p;
 
if (qt_inconsistente_w > 0) then 
	ds_mensagem_p := obter_texto_dic_objeto(137300, wheb_usuario_pck.get_nr_seq_idioma, null);
end if;
 
end;
$body$
