SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_gerar_mat_envio_intercamb ( cd_material_p text, ie_tipo_conversao_p text, ie_tipo_tabela_p text, ie_tipo_despesa_p text, nm_usuario_p text, cd_servico_conversao_p INOUT bigint, ds_material_p INOUT text) AS $body$
DECLARE


nr_seq_material_w		bigint;
cd_material_a900_w		varchar(30);
cd_mat_conversao_w		varchar(50);
ie_somente_codigo_w		pls_regra_conv_mat_interc.ie_somente_codigo%type;


BEGIN

if (ie_tipo_conversao_p	= 'R') then
	cd_material_a900_w	:= cd_material_p;
	SELECT * FROM pls_obter_mat_a900_vigente( nr_seq_material_w, clock_timestamp(), cd_material_a900_w, null) INTO STRICT  nr_seq_material_w, cd_material_a900_w;

	if (coalesce(nr_seq_material_w,0)	<> 0) then
		begin
			select	cd_material_ops
			into STRICT	cd_servico_conversao_p
			from	pls_material
			where	nr_sequencia = nr_seq_material_w;
		exception
		when others then
			cd_servico_conversao_p	:= '';
		end;
	elsif (coalesce(nr_seq_material_w,0)	= 0) then
		SELECT * FROM ptu_obter_material_conversao(	cd_material_p, ie_tipo_tabela_p, clock_timestamp(), ie_tipo_conversao_p, 'S', ie_tipo_despesa_p, null, nr_seq_material_w, cd_mat_conversao_w, ie_somente_codigo_w) INTO STRICT nr_seq_material_w, cd_mat_conversao_w, ie_somente_codigo_w;

		begin
			select	cd_material_ops
			into STRICT	cd_servico_conversao_p
			from	pls_material
			where	nr_sequencia = nr_seq_material_w;
		exception
		when others then
			cd_servico_conversao_p	:= '';
		end;
	end if;
	
elsif (ie_tipo_conversao_p	= 'E') then
	cd_servico_conversao_p	:= '';
	ds_material_p           := '';

	select	max(nr_sequencia)
	into STRICT	nr_seq_material_w
	from	pls_material
	where	cd_material_ops = cd_material_p
	and 	coalesce(ie_sistema_ant, 'N') = 'N'
	and 	trunc(clock_timestamp()) between coalesce(dt_inclusao, trunc(clock_timestamp())) and  coalesce(dt_exclusao, trunc(clock_timestamp()))
	and 	ie_situacao = 'A'
	and 	coalesce(dt_limite_utilizacao,trunc(clock_timestamp())) >= trunc(clock_timestamp());
	

	if (nr_seq_material_w IS NOT NULL AND nr_seq_material_w::text <> '') then

		select	max(cd_material_a900)
		into STRICT	cd_material_a900_w
		from	pls_material_a900
		where	nr_seq_material = nr_seq_material_w
		and	PKG_DATE_UTILS.start_of(clock_timestamp(),'DAY',0) between coalesce(dt_inicio_vigencia,clock_timestamp()) and coalesce(dt_fim_vigencia,clock_timestamp())
		order by dt_inicio_vigencia;
		
		cd_servico_conversao_p := somente_numero(cd_material_a900_w);

		if (coalesce(cd_servico_conversao_p,0) = 0) then
			SELECT * FROM ptu_obter_material_conversao(	cd_material_p, ie_tipo_tabela_p, clock_timestamp(), ie_tipo_conversao_p, 'S', ie_tipo_despesa_p, null, nr_seq_material_w, cd_mat_conversao_w, ie_somente_codigo_w) INTO STRICT nr_seq_material_w, cd_mat_conversao_w, ie_somente_codigo_w;

			begin
				select	cd_material
				into STRICT	cd_servico_conversao_p
				from	pls_material_unimed
				where	nr_sequencia	= nr_seq_material_w;
			exception
			when others then
				cd_servico_conversao_p	:= '';
			end;
		end if;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
