CREATE OR REPLACE PROCEDURE ptu_gerar_nota_compl_serv ( nr_seq_cobranca_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_conta_w			bigint;
nr_seq_servico_w		bigint;
nr_seq_lote_w			integer;

c01 CURSOR FOR
	SELECT	c.nr_seq_lote,
		b.nr_sequencia
	from	pls_conta		a,
		pls_lote_protocolo	b,
		pls_protocolo_conta	c
	where	b.nr_sequencia		= c.nr_seq_lote
	and	c.nr_sequencia		= a.nr_seq_protocolo
	and	a.nr_seq_serv_pre_pagto	= nr_seq_servico_w;


BEGIN

select	nr_seq_serv_pre_pagto
into STRICT	nr_seq_servico_w
from	ptu_nota_cobranca
where	nr_sequencia	= nr_seq_cobranca_p;

open c01;
loop
fetch c01 into
	nr_seq_lote_w,
	nr_seq_conta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	insert into ptu_nota_complemento(nr_sequencia, nr_seq_nota_cobr, nr_lote,
		nr_nota, ie_tipo_complemento, ds_complemento,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
		nm_usuario_nrec)
	values (nextval('ptu_nota_complemento_seq'), nr_seq_cobranca_p, nr_seq_lote_w,
		nr_seq_conta_w, 1, null,
		clock_timestamp(), nm_usuario_p, clock_timestamp(),
		nm_usuario_p);

	end;
end loop;
close C01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
