CREATE OR REPLACE PROCEDURE ptu_gerar_novo_beneficiario ( nm_beneficiario_p text, cd_unimed_benef_p text, cd_usuario_plano_p text, cd_unimed_exec_p text, dt_validade_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text, nr_via_cartao_p bigint, nr_seq_segurado_p INOUT bigint, nr_seq_plano_p pls_plano.nr_sequencia%type default null) AS $body$
DECLARE


nr_seq_inclusao_w		bigint;
nr_seq_plano_w			bigint;
qt_registros_w			bigint;
nr_seq_congenere_w		bigint;
				

BEGIN

if (nr_seq_plano_p IS NOT NULL AND nr_seq_plano_p::text <> '') then
	nr_seq_plano_w := nr_seq_plano_p;
else
	/*aaschlote - Ajustei para colocar o produto correto*/

	begin
	nr_seq_plano_w	:= (pls_obter_nome_empresa_cart(cd_usuario_plano_p,'P',cd_estabelecimento_p))::numeric;
	exception
	when others then
	select	count(1)
	into STRICT	qt_registros_w
	from	pls_plano
	where	nr_sequencia	= 820;

	if (qt_registros_w = 0) then
		nr_seq_plano_w	:= 820;
	end if;
	end;
end if;

select	nextval('pls_inclusao_beneficiario_seq')
into STRICT	nr_seq_inclusao_w
;	

insert	into pls_inclusao_beneficiario(nr_sequencia, dt_nascimento, nm_usuario,
	 cd_estabelecimento, nm_pessoa_fisica, nr_seq_plano,
	 dt_atualizacao)
values (nr_seq_inclusao_w, null, nm_usuario_p,
	 cd_estabelecimento_p, nm_beneficiario_p, nr_seq_plano_w,
	 clock_timestamp());

begin
	select	max(nr_sequencia)
	into STRICT	nr_seq_congenere_w
	from	pls_congenere
	where	(cd_cooperativa)::numeric  = (cd_unimed_benef_p)::numeric;
exception
when others then
	nr_seq_congenere_w	:= null;
end;
	
nr_seq_segurado_p := pls_gerar_segurado_congenere('', nr_seq_inclusao_w, cd_unimed_exec_p, cd_usuario_plano_p, dt_validade_p, nm_usuario_p, cd_estabelecimento_p, nr_seq_congenere_w, nr_via_cartao_p, 3, nr_seq_segurado_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
