SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_gerar_pagador_intercambio ( nr_seq_intercambio_p bigint, ie_tipo_contrato_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

			
nr_seq_congenere_w		bigint;
nr_seq_oper_congenere_w		bigint;
cd_pessoa_fisica_w		varchar(10);
cd_cgc_w			varchar(14);
ie_tipo_pagador_w		varchar(1);
nr_seq_pagador_w		bigint;
cd_cgc_pagador_fund_w		varchar(14);
ie_endereco_boleto_w		varchar(3);
qt_pagador_w			bigint;
qt_pagador_ww			bigint;
cd_tipo_portador_param_w	integer;
cd_portador_param_w		bigint;
nr_seq_conta_banco_param_w	bigint;
cd_condicao_pagamento_w		bigint;
ie_gerar_nota_titulo_param_w	varchar(2);
dt_contrato_w			timestamp;
dt_dia_vencimento_w		bigint;
ie_tipo_contrato_w		varchar(10);
nr_seq_grupo_intercambio_w	bigint;
qt_registros_w			bigint;
ie_possui_pagador_grupo_w	varchar(10);
ie_gerar_pagador_w		varchar(10);
IE_VINCULAR_PAG_COOP_A100_w	varchar(10);

C01 CURSOR FOR
	SELECT	DT_DIA_VENCIMENTO
	from	PTU_REGRA_INF_FIN_PAGADOR
	where	((ie_tipo_contrato	= ie_tipo_contrato_p) or (coalesce(ie_tipo_contrato::text, '') = ''))
	and	((nr_seq_congenere	= nr_seq_congenere_w) or (coalesce(nr_seq_congenere::text, '') = ''))
	and	ie_situacao		= 'A'
	order by CASE WHEN coalesce(nr_seq_congenere::text, '') = '' THEN -1  ELSE 1 END ,
		CASE WHEN ie_tipo_contrato='' THEN -1  ELSE 1 END;


BEGIN
select	nr_seq_congenere,
	nr_seq_oper_congenere,
	cd_pessoa_fisica,
	cd_cgc,
	DT_INCLUSAO,
	nr_seq_grupo_intercambio
into STRICT	nr_seq_congenere_w,
	nr_seq_oper_congenere_w,
	cd_pessoa_fisica_w,
	cd_cgc_w,
	dt_contrato_w,
	nr_seq_grupo_intercambio_w
from	pls_intercambio
where	nr_sequencia	= nr_seq_intercambio_p;

if (nr_seq_congenere_w IS NOT NULL AND nr_seq_congenere_w::text <> '') then
	select	max(cd_cgc)
	into STRICT	cd_cgc_pagador_fund_w
	from	pls_congenere
	where	nr_sequencia	= nr_seq_congenere_w;
elsif (nr_seq_oper_congenere_w IS NOT NULL AND nr_seq_oper_congenere_w::text <> '') then
	select	max(cd_cgc)
	into STRICT	cd_cgc_pagador_fund_w
	from	pls_congenere
	where	nr_sequencia	= nr_seq_oper_congenere_w;
end if;	

select	count(*)
into STRICT	qt_pagador_w
from	pls_contrato_pagador
where	nr_seq_pagador_intercambio	= nr_seq_intercambio_p;

if (qt_pagador_w = 0) then
	ie_tipo_pagador_w	:= 'P';
elsif (qt_pagador_w > 0) then
	ie_tipo_pagador_w	:= 'S';
end if;

select	CASE WHEN cd_pessoa_fisica_w='' THEN 'PJ'  ELSE 'PFR' END
into STRICT	ie_endereco_boleto_w
;

dt_contrato_w	:= trunc(dt_contrato_w,'Month');

select	max(a.cd_tipo_portador),
	max(a.cd_portador),
	max(a.nr_seq_conta_banco),
	max(a.ie_geracao_nota_titulo),
	max(a.cd_condicao_pagamento),
	max(a.IE_VINCULAR_PAG_COOP_A100)
into STRICT	cd_tipo_portador_param_w,
	cd_portador_param_w,
	nr_seq_conta_banco_param_w,
	ie_gerar_nota_titulo_param_w,
	cd_condicao_pagamento_w,
	IE_VINCULAR_PAG_COOP_A100_w
from	pls_parametros a
where	a.cd_estabelecimento	= cd_estabelecimento_p;

ie_vincular_pag_coop_a100_w	 := coalesce(ie_vincular_pag_coop_a100_w,'N');

open C01;
loop
fetch C01 into	
	dt_dia_vencimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

dt_dia_vencimento_w		:= coalesce(dt_dia_vencimento_w,15);
ie_possui_pagador_grupo_w	:= 'N';
ie_gerar_pagador_w		:= 'S';

/*Verificar regras do grupo de intercâmbio*/

if (nr_seq_grupo_intercambio_w IS NOT NULL AND nr_seq_grupo_intercambio_w::text <> '') then
	select	count(1)
	into STRICT	qt_registros_w
	from	PLS_REGRA_GRUPO_INTER
	where	nr_sequencia	= nr_seq_grupo_intercambio_w
	and	IE_GRUPO_PAGADOR = 'S';
	
	if (qt_registros_w > 0) then
		ie_possui_pagador_grupo_w	:= 'S';
		select	count(1)
		into STRICT	qt_registros_w
		from	pls_intercambio		b,
			pls_contrato_pagador	a
		where	a.nr_seq_pagador_intercambio 	= b.nr_sequencia
		and	b.nr_seq_grupo_intercambio	= nr_seq_grupo_intercambio_w
		and	a.IE_PAG_REF_GRUPO_INTERCAMBIO	= 'S';
		
		if (qt_registros_w > 0) then
			ie_gerar_pagador_w	:= 'N';
		end if;
	end if;
end if;

/*aaschlote 28/01/2013 OS 529310 - Caso o beneficiário receba o pagador de contrato de A100, não gera pagador no contrato de intercâmbio*/

if (ie_vincular_pag_coop_a100_w = 'S') and (ie_tipo_contrato_p	= 'I') then
	ie_gerar_pagador_w	:= 'N';
end if;

if (ie_gerar_pagador_w = 'S') then
	select	count(*)
	into STRICT	qt_pagador_ww
	from	pls_contrato_pagador
	where	nr_seq_pagador_intercambio	= nr_seq_intercambio_p
	and	cd_cgc				= cd_cgc_pagador_fund_w;
	
	if (qt_pagador_ww	= 0) then
	
		select	nextval('pls_contrato_pagador_seq')
		into STRICT	nr_seq_pagador_w
		;
		
		insert into	pls_contrato_pagador(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_pagador_intercambio,cd_cgc,ie_tipo_pagador,ie_endereco_boleto,ie_envia_cobranca,
				ie_calc_primeira_mens,ie_calculo_proporcional,ie_notificacao,ie_taxa_emissao_boleto,
				IE_PAG_REF_GRUPO_INTERCAMBIO, ie_receber_sms)
		values (	nr_seq_pagador_w,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				nr_seq_intercambio_p,cd_cgc_pagador_fund_w,ie_tipo_pagador_w,ie_endereco_boleto_w,'A',
				'I','M','NR','N',
				ie_possui_pagador_grupo_w, 'S');
				
		insert into pls_contrato_pagador_fin(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_pagador,dt_inicio_vigencia,dt_dia_vencimento,nr_seq_forma_cobranca,ie_mes_vencimento,
				cd_condicao_pagamento,ie_portador_exclusivo,ie_geracao_nota_titulo,ie_destacar_reajuste,ie_gerar_cobr_escrit,
				cd_tipo_portador,cd_portador,nr_seq_conta_banco)
		values (	nextval('pls_contrato_pagador_fin_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				nr_seq_pagador_w,dt_contrato_w,dt_dia_vencimento_w,'1','A',
				cd_condicao_pagamento_w,'S',ie_gerar_nota_titulo_param_w,'N','S',
				cd_tipo_portador_param_w,cd_portador_param_w,nr_seq_conta_banco_param_w);		
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ptu_gerar_pagador_intercambio ( nr_seq_intercambio_p bigint, ie_tipo_contrato_p text, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

