SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_gerar_pedido_status ( nr_seq_pedido_status_p ptu_pedido_status.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_origem_p INOUT ptu_controle_execucao.nr_sequencia%type) AS $body$
DECLARE

 
C01 CURSOR( nr_seq_pedido_status_pc 	ptu_pedido_status.nr_sequencia%type) FOR 
	SELECT	nr_seq_requisicao, 
		nr_seq_guia, 
		cd_unimed_executora, 
		(SELECT	max(b.nr_sequencia) 
		from	ptu_pedido_compl_aut b 
		where	b.nr_seq_execucao	= a.nr_transacao_uni_exec  
		and	b.cd_unimed_executora	= a.cd_unimed_executora) nr_seq_pedido_compl, 
		(select	max(b.nr_sequencia) 
		from	ptu_pedido_autorizacao b 
		where	b.nr_seq_execucao	= a.nr_transacao_uni_exec  
		and	b.cd_unimed_executora	= a.cd_unimed_executora) nr_seq_pedido_aut 
	from	ptu_pedido_status a 
	where	a.nr_sequencia = nr_seq_pedido_status_pc;

BEGIN 
 
--Carregar as informações da Guia e da Requisição 
for c01_w in C01( nr_seq_pedido_status_p ) loop 
	 
	if (c01_w.nr_seq_guia IS NOT NULL AND c01_w.nr_seq_guia::text <> '') then 
		CALL pls_guia_gravar_historico( c01_w.nr_seq_guia, 2, 'Recebido pedido de status da Unimed '||c01_w.cd_unimed_executora, '', nm_usuario_p);
	elsif (c01_w.nr_seq_requisicao IS NOT NULL AND c01_w.nr_seq_requisicao::text <> '') then 
		CALL pls_requisicao_gravar_hist( c01_w.nr_seq_requisicao, 'L', 'Recebido pedido de status da Unimed '||c01_w.cd_unimed_executora, null, nm_usuario_p);
	end if;	
	 
	--Rotina utilizada para gerar a resposta para o pedido de status da transação 
	nr_seq_origem_p := ptu_gestao_env_res_status_tran( coalesce(c01_w.nr_seq_pedido_aut,c01_w.nr_seq_pedido_compl), nr_seq_pedido_status_p, cd_estabelecimento_p, nm_usuario_p, nr_seq_origem_p);
end loop;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
