CREATE OR REPLACE PROCEDURE ptu_gerar_plano ( nr_seq_empresa_p bigint, nr_seq_intercambio_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_gerarcao_p text) AS $body$
DECLARE


/*
ie_gerarcao_p
S - Gerar todos os beneficiários da empresa
N - Não gerar os beneficiários da empresa
*/
				
				
nr_sequencia_w			bigint;
cd_plano_origem_w		varchar(6);
nr_ind_reembolso_w		bigint;
nr_seq_plano_w			bigint;
cd_estabelecimento_w		bigint;
ie_situacao_w			varchar(2) := 'A';
nr_seq_plano_ww			bigint;
qt_plano_w			bigint;
ie_repasse_w			varchar(10);

C01 CURSOR FOR
	SELECT	nr_seq_plano
	from	ptu_intercambio_benef
	where	nr_seq_empresa	= nr_seq_empresa_p
	and	(nr_seq_plano IS NOT NULL AND nr_seq_plano::text <> '')
	and	ie_status = 'A'
	group by nr_seq_plano;


BEGIN

select	max(ie_repasse)
into STRICT	ie_repasse_w
from	ptu_intercambio_benef
where	nr_seq_empresa	= nr_seq_empresa_p;

open C01;
loop
fetch C01 into
	nr_seq_plano_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	select	count(*)
	into STRICT	qt_plano_w
	from	pls_intercambio_plano
	where	nr_seq_intercambio	= nr_seq_intercambio_p
	and	nr_seq_plano		= nr_seq_plano_w;
	
	if (qt_plano_w = 0) then
		select	nextval('pls_intercambio_plano_seq')
		into STRICT	nr_seq_plano_ww
		;
		
		insert into pls_intercambio_plano(nr_sequencia, nr_seq_intercambio, nr_seq_plano,
			cd_plano_origem, dt_atualizacao, nm_usuario, 
			dt_atualizacao_nrec, nm_usuario_nrec)
		values (nr_seq_plano_ww, nr_seq_intercambio_p, nr_seq_plano_w,
			cd_plano_origem_w, clock_timestamp(), nm_usuario_p,
			clock_timestamp(), nm_usuario_p);

		if (ie_repasse_w = 'P') then
			CALL ptu_gerar_tabelas_intercambio(nr_seq_plano_ww,nr_seq_empresa_p,cd_estabelecimento_p,nm_usuario_p);
		end if;
	end if;
		
	if (ie_gerarcao_p = 'S') then
		CALL ptu_gerar_benef(nr_seq_empresa_p, nr_seq_plano_w, nr_seq_intercambio_p, nm_usuario_p,cd_estabelecimento_p);
	end if;
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
