SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_gerar_prestador_rede_ref ( nr_seq_prestador_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_prestador_w	bigint;
cd_rede_w		varchar(5);
ds_rede_w		varchar(40);

c01 CURSOR FOR
	SELECT	substr(b.cd_rede,1,5),
		elimina_acentuacao(replace(substr(b.ds_rede,1,40),chr(160),'')) -- Replace do chr(160) Ã© para retirar um lixo que vem no campo
	from	ptu_rede_referenciada b,
		pls_prestador_rede_ref a
	where	a.cd_rede	= b.cd_rede
	and	a.nr_seq_prestador	= nr_seq_prestador_w;


BEGIN

if (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') then

	select	a.nr_seq_prestador
	into STRICT	nr_seq_prestador_w
	from	ptu_prestador a
	where	a.nr_sequencia	= nr_seq_prestador_p;

	if (nr_seq_prestador_w IS NOT NULL AND nr_seq_prestador_w::text <> '') then
		open c01;
		loop
		fetch c01 into
			cd_rede_w,
			ds_rede_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			insert into ptu_prestador_rede_ref(nr_sequencia,
				nr_seq_prestador,
				cd_rede,
				nm_rede,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
			values (nextval('ptu_prestador_rede_ref_seq'),
				nr_seq_prestador_p,
				cd_rede_w,
				ds_rede_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p);
			end;
		end loop;
		close c01;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
