CREATE OR REPLACE PROCEDURE ptu_gerar_resp_auditoria ( nr_seq_auditoria_p bigint, nr_seq_requisicao_p bigint, qt_itens_neg_p INOUT text, ie_glosa_inconsist_vincul_p INOUT text, ie_ocorr_inconsist_vincul_p INOUT text, ie_intercambio_p INOUT text, nr_seq_compl_p INOUT text) AS $body$
DECLARE


qt_existe_w		bigint;
ie_glosa_acao_ptu_w	varchar(2);
ie_ocorr_acao_ptu_w	varchar(2);
ie_intercamb_coop_w	varchar(2);
ie_transacao_compl_ptu_w	varchar(10);


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	pls_auditoria_item
where	nr_seq_auditoria = nr_seq_auditoria_p
and	ie_status = 'N';

if (qt_existe_w > 0) then

	select	substr(pls_obter_se_glosa_acao_ptu(null, nr_seq_requisicao_p, null),1,4),
		substr(pls_obter_se_ocorr_acao_ptu(null, nr_seq_requisicao_p,null),1,4)
	into STRICT	ie_glosa_acao_ptu_w,
		ie_ocorr_acao_ptu_w
	;

end if;


select	pls_obter_tipo_intercamb_coop(nr_seq_requisicao_p,null)
into STRICT	ie_intercamb_coop_w
;

select	pls_obter_transacao_compl_ptu(nr_seq_auditoria_p)
into STRICT	ie_transacao_compl_ptu_w
;

qt_itens_neg_p	:= qt_existe_w;
ie_glosa_inconsist_vincul_p := ie_glosa_acao_ptu_w;
ie_ocorr_inconsist_vincul_p := ie_ocorr_acao_ptu_w;
ie_intercambio_p := ie_intercamb_coop_w;
nr_seq_compl_p := ie_transacao_compl_ptu_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
