SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ptu_gravar_arquivo_gerado_a800 ( nr_sequencial_p bigint, ie_tipo_p bigint, nm_arquivo_p text, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		bigint;

/*
ie_tipo_p
1 - Lote
2 - Item
*/
BEGIN

if (ie_tipo_p = 1) then
	select	count(*)
	into STRICT	qt_registros_w
	from	PTU_FATURA_PRE
	where	nr_seq_lote	= nr_sequencial_p
	and	coalesce(dt_geracao_arquivo::text, '') = '';

	/*Atualizar a data de geração do arquivo,. caso todos os arquivos foram gerados*/

	if (qt_registros_w = 0) then
		update	PTU_FATURA_PRE_LOTE
		set	dt_geracao_arquivo	= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia		= nr_sequencial_p;
	end if;


elsif (ie_tipo_p = 2) then
	update	ptu_fatura_pre
	set	nm_arquivo		= nm_arquivo_p,
		dt_geracao_arquivo	= clock_timestamp(),
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_sequencial_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
