CREATE OR REPLACE PROCEDURE ptu_gravar_cartao_retorno ( nr_seq_intercambio_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

			
nr_seq_segurado_w		ptu_intercambio_benef.nr_seq_segurado%type;
cd_usuario_plano_cad_w		ptu_retorno_mov_benef.cd_usuario_plano_cad%type;
cd_unimed_cad_w			ptu_retorno_mov_benef.cd_unimed_cad%type;
			
C01 CURSOR FOR
	SELECT	d.nr_seq_segurado,
		c.cd_usuario_plano_cad,
		lpad(c.cd_unimed_cad,4,'0')
	from	ptu_intercambio_benef		d,
		ptu_retorno_mov_benef		c,
		ptu_retorno_movimentacao	b,
		ptu_intercambio			a
	where	d.nr_sequencia			= c.nr_seq_benef_intercambio
	and	c.nr_seq_retorno_mov		= b.nr_sequencia
	and	b.nr_seq_intercambio		= a.nr_sequencia
	and	a.nr_sequencia			= nr_seq_intercambio_p
	and	c.cd_inconsistencia		in (3201,3202)
	and	(c.nr_seq_benef_intercambio IS NOT NULL AND c.nr_seq_benef_intercambio::text <> '')
	and	(d.nr_seq_segurado IS NOT NULL AND d.nr_seq_segurado::text <> '');

c02 CURSOR(	cd_unimed_cad_pc	text,
		nr_seq_segurado_pc	pls_segurado.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia
	from	pls_segurado_repasse a,
		pls_congenere b
	where	b.nr_sequencia = a.nr_seq_congenere
	and	a.nr_seq_segurado = nr_seq_segurado_pc
	and	lpad(b.cd_cooperativa,4,'0') = cd_unimed_cad_pc
	and	coalesce(a.nr_cartao_intercambio::text, '') = '';
	
BEGIN

open C01;
loop
fetch C01 into	
	nr_seq_segurado_w,
	cd_usuario_plano_cad_w,
	cd_unimed_cad_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	pls_segurado_carteira
	set	nr_cartao_intercambio	= cd_unimed_cad_w||cd_usuario_plano_cad_w,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_segurado		= nr_seq_segurado_w;
	
	for r_c02_w in c02(	cd_unimed_cad_w,
				nr_seq_segurado_w) loop
		begin
		update	pls_segurado_repasse
		set	nr_cartao_intercambio = cd_unimed_cad_w||cd_usuario_plano_cad_w,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = r_c02_w.nr_sequencia;
		end;
	end loop;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
