CREATE OR REPLACE PROCEDURE ptu_gravar_log_erro_inesperado ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, cd_mens_excecao_p bigint, cd_transacao_p text, nm_usuario_p text) AS $body$
DECLARE


ds_observacao_w			varchar(4000);
ds_inconsistencia_w		varchar(255);

nr_seq_inconsistencia_w		bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_inconsistencia_w
from	ptu_inconsistencia
where	cd_inconsistencia = cd_mens_excecao_p;

if (nr_seq_inconsistencia_w IS NOT NULL AND nr_seq_inconsistencia_w::text <> '') then
	ds_inconsistencia_w	:= substr(pls_obter_dados_insconsitencia(nr_seq_inconsistencia_w,'D'),1,255);
end if;

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	ds_observacao_w	:= substr('Erro inesperado '||cd_mens_excecao_p||' - '||ds_inconsistencia_w||', gerado para a Guia: '||nr_seq_guia_p,1,4000);
elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	ds_observacao_w	:= substr('Erro inesperado '||cd_mens_excecao_p||' - '||ds_inconsistencia_w||', gerado para a Requisição: '||nr_seq_requisicao_p,1,4000);
else
	ds_observacao_w	:= substr('Erro inesperado '||cd_mens_excecao_p||' - '||ds_inconsistencia_w,1,4000);
end if;

insert	into pls_log_scs(nr_sequencia, dt_processo, dt_atualizacao,
	 nm_usuario, ds_diretorio_arquivo, ds_diretorio_backup,
	 ie_backup, cd_transacao, ds_observacao,
	 ie_tipo_log)
values (nextval('pls_log_scs_seq'), clock_timestamp(), clock_timestamp(),
	 nm_usuario_p, '', '',
	 'N', cd_transacao_p, ds_observacao_w,
	 'M');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
