nm_beneficiario_w		varchar(25);
sobrenome_benef_w		varchar(10);
dt_auxiliar_w			timestamp;
dt_nascimento_w			timestamp;
nr_seq_cons_benef_w		bigint;
				
c01 CURSOR FOR 
	SELECT	nr_seq_importacao, 
