CREATE OR REPLACE PROCEDURE ptu_imp_req_contagem_benef_v50 ( ds_arquivo_p text, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_pedido_cont_p INOUT bigint) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Importação contagem benef 
 
Rotina utilizada nas transações ptu via scs homologadas com a unimed brasil. 
quando for alterar, favor verificar com o análista responsável para a realização de testes. 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ X ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
nr_seq_import_w			bigint;
ds_conteudo_w			varchar(4000);
cd_transacao_w			varchar(255);
ie_tipo_cliente_w		varchar(255);
cd_unimed_requisitante_w	bigint;
cd_unimed_destino_w		bigint;
dt_ano_referencia_w		varchar(255);
dt_mes_referencia_w		varchar(255);
nr_seq_pedido_cont_w		bigint;
nr_versao_w			varchar(3);

c01 CURSOR FOR 
	SELECT	nr_seq_importacao, 
		ds_valores 
	from	w_scs_importacao 
	where	nm_usuario	= nm_usuario_p 
	order by nr_seq_importacao;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_import_w, 
	ds_conteudo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (substr(ds_conteudo_w,1,4)	<> 'FIM$') then 
		if (nr_seq_import_w	= 1) then 
			cd_transacao_w			:= substr(ds_conteudo_w,1,5);
			ie_tipo_cliente_w		:= trim(both substr(ds_conteudo_w,6,15));
			cd_unimed_requisitante_w	:= trim(both substr(ds_conteudo_w,21,4));
			cd_unimed_destino_w		:= trim(both substr(ds_conteudo_w,25,4));
			dt_ano_referencia_w		:= trim(both substr(ds_conteudo_w,29,4));
			dt_mes_referencia_w		:= trim(both substr(ds_conteudo_w,33,2));
			nr_versao_w			:= trim(both substr(ds_conteudo_w,35,3));
			 
			select	nextval('ptu_pedido_contagem_benef_seq') 
			into STRICT	nr_seq_pedido_cont_w 
			;
			 
			if (ie_tipo_cliente_w	= 'UNIMED') then 
				ie_tipo_cliente_w	:= 'U';
			elsif (ie_tipo_cliente_w	= 'PORTAL') then 
				ie_tipo_cliente_w	:= 'P';
			elsif (ie_tipo_cliente_w	= 'PRESTADOR') then 
				ie_tipo_cliente_w	:= 'R';
			end if;
			 
			insert	into ptu_pedido_contagem_benef(nr_sequencia, cd_transacao, cd_unimed_destino, 
				 cd_unimed_requisitante, ds_ano_referencia, ds_mes_referencia, 
				 ie_tipo_cliente, dt_atualizacao_nrec, dt_atualizacao, 
				 nm_usuario, nm_usuario_nrec, dt_ano_referencia, 
				 dt_mes_referencia, nr_versao) 
			values (nr_seq_pedido_cont_w, cd_transacao_w, cd_unimed_destino_w, 
				 cd_unimed_requisitante_w, dt_ano_referencia_w, dt_mes_referencia_w, 
				 ie_tipo_cliente_w, clock_timestamp(), clock_timestamp(), 
				 nm_usuario_p, nm_usuario_p, clock_timestamp(), 
				 clock_timestamp(), nr_versao_w);
		end if;
	end if;
	end;
end loop;
close C01;
 
CALL ptu_gest_env_res_cont_benef(nr_seq_pedido_cont_w, cd_estabelecimento_p, nm_usuario_p);
nr_seq_pedido_cont_p	:= nr_seq_pedido_cont_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
