CREATE OR REPLACE PROCEDURE ptu_liberar_movto_prestador ( nr_seq_movimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_inconsistencias_w	integer;
qt_brancos_w		integer;


BEGIN

select	count(*)
into STRICT	qt_inconsistencias_w
from	ptu_prestador
where	nr_seq_movimento	= nr_seq_movimento_p
and	ie_consistente		= 'IN';

select	count(*)
into STRICT	qt_brancos_w
from	ptu_prestador
where	nr_seq_movimento	= nr_seq_movimento_p
and	coalesce(ie_consistente::text, '') = '';

if (qt_brancos_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(189039);

elsif (qt_inconsistencias_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(189040);

else
	update	ptu_movimento_prestador
	set	dt_liberacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_movimento_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
