CREATE OR REPLACE PROCEDURE ptu_obter_dados_arquivo_exp ( nr_sequencia_p bigint, nr_seq_classificacao_p bigint, cd_interface_p INOUT bigint, cd_relatorio_p INOUT bigint, nm_arquivo_p INOUT text) AS $body$
DECLARE

 
cd_interface_w	bigint;
cd_relatorio_w	integer;
nm_arquivo_w	varchar(12);


BEGIN 
if (nr_seq_classificacao_p IS NOT NULL AND nr_seq_classificacao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
	begin 
	cd_interface_w	:= 0;
