	;
	 
	select	coalesce(ptu_obter_interf_sca_classif(nr_seq_classificacao_p),0) 
	into STRICT	cd_relatorio_w 
	;
	 
	select	coalesce(ptu_obter_rel_sca_classif(nr_seq_classificacao_p), 0) 
	into STRICT	cd_relatorio_w 
	;
		 
	cd_interface_p := cd_interface_w;
	cd_relatorio_p := cd_relatorio_w;
	nm_arquivo_p  := nm_arquivo_w;
 
	end;
end if;
 
end;
