CREATE OR REPLACE PROCEDURE ptu_obter_material_conversao ( cd_material_p bigint, ie_tipo_tabela_p bigint, dt_referencia_p timestamp, ie_envio_receb_p text, ie_tipo_transacao_p text, ie_tipo_despesa_p text, ie_tipo_intercambio_p pls_regra_conv_mat_interc.ie_tipo_intercambio%type, nr_seq_material_p INOUT bigint, cd_material_conv_p INOUT text, ie_somente_codigo_p INOUT text) AS $body$
DECLARE

/*
IE_ENVIO_RECEB_P
E -  Envio
R - Recebimento

IE_TIPO_TRANSACAO_P
A - A500
7 - A700
S - SCS
*/
nr_seq_material_w	pls_regra_conv_mat_interc.nr_seq_material%type	:= null;
cd_material_conv_w	pls_regra_conv_mat_interc.cd_material_a900%type	:= null;
ie_somente_codigo_w	pls_regra_conv_mat_interc.ie_somente_codigo%type:= null;

C01 CURSOR FOR
	SELECT	a.nr_seq_material,
		a.cd_material_a900
	from	pls_regra_conv_mat_interc	a
	where (coalesce(a.ie_tipo_tabela::text, '') = ''	or 	a.ie_tipo_tabela	= ie_tipo_tabela_p)
	and	((ie_ptu_a500		= 'S'	and	ie_tipo_transacao_p	= 'A')
	or (ie_ptu_a700		= 'S'	and	ie_tipo_transacao_p	= '7')
	or (ie_ptu_a1200		= 'S'	and	ie_tipo_transacao_p	= '8')
	or (ie_ptu_scs		= 'S'	and	ie_tipo_transacao_p	= 'S'))
	and	((coalesce(a.cd_material_inicial::text, '') = ''	and	coalesce(cd_material_final::text, '') = '') or (cd_material_p		between(cd_material_inicial)		and (cd_material_final)))
	and	ie_envio_receb		= 'R'
	and	((coalesce(a.ie_tipo_despesa_mat::text, '') = '') or (a.ie_tipo_despesa_mat = ie_tipo_despesa_p) or coalesce(ie_tipo_despesa_p::text, '') = '')
	and	dt_referencia_p between(dt_inicio_vigencia) and (fim_dia(coalesce(dt_fim_vigencia,dt_referencia_p)))
	and	((coalesce(ie_tipo_intercambio::text, '') = '') or (ie_tipo_intercambio = 'A') or (ie_tipo_intercambio	= ie_tipo_intercambio_p))
	order by
		coalesce(ie_tipo_tabela,0),
		coalesce(cd_material_inicial,0),
		coalesce(cd_material_final,0);

C02 CURSOR FOR
	SELECT	a.nr_seq_material_unimed,
		a.cd_material_a900,
		a.ie_somente_codigo
	from	pls_regra_conv_mat_interc	a
	where (coalesce(a.ie_tipo_tabela::text, '') = ''	or 	a.ie_tipo_tabela	= ie_tipo_tabela_p)
	and	((ie_ptu_a500		= 'S'	and	ie_tipo_transacao_p	= 'A')
	or (ie_ptu_a700		= 'S'	and	ie_tipo_transacao_p	= '7')
	or (ie_ptu_a1200		= 'S'	and	ie_tipo_transacao_p	= '8')
	or (ie_ptu_scs		= 'S'	and	ie_tipo_transacao_p	= 'S'))
	and	((coalesce(a.cd_material_inicial::text, '') = ''	and	coalesce(cd_material_final::text, '') = '') or (cd_material_p		between(cd_material_inicial)		and (cd_material_final)))
	and	ie_envio_receb		= 'E'
	and	((coalesce(a.ie_tipo_despesa_mat::text, '') = '') or (a.ie_tipo_despesa_mat = ie_tipo_despesa_p) or coalesce(ie_tipo_despesa_p::text, '') = '')
	and	dt_referencia_p between dt_inicio_vigencia and fim_dia(coalesce(dt_fim_vigencia,dt_referencia_p))
	and	((coalesce(ie_tipo_intercambio::text, '') = '') or (ie_tipo_intercambio = 'A') or (ie_tipo_intercambio	= ie_tipo_intercambio_p))
	order by
		coalesce(ie_tipo_tabela,0),
		coalesce(cd_material_inicial,0),
		coalesce(cd_material_final,0);


BEGIN
if (ie_envio_receb_p	= 'R') then
	/* Retorna o seq da tabela PLS_MATERIAL */

	open C01;
	loop
	fetch C01 into
		nr_seq_material_w,
		cd_material_conv_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		null;
		end;
	end loop;
	close C01;
elsif (ie_envio_receb_p	= 'E') then
	/* Retornar o seq da tabela PLS_MATERIAL_UNIMED */

	open C02;
	loop
	fetch C02 into
		nr_seq_material_w,
		cd_material_conv_w,
		ie_somente_codigo_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
	end loop;
	close C02;
end if;

ie_somente_codigo_p	:= ie_somente_codigo_w;
nr_seq_material_p	:= nr_seq_material_w;
cd_material_conv_p	:= cd_material_conv_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
